.login {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100%;
  background-size: cover;
}

.title {
  padding-top: 30px;
  padding-bottom: 20px;
  display: flex;
  justify-content: center;

  .logo {
    width: 95px;
    height: 49px;
  }

  .text {
    //width: 173px;
    //height:27px;
    font-size: 28px;
    padding-left: 10px;
    font-family: Source Han Sans SC;
    font-weight: bold;
    color: rgba(101, 101, 101, 1);
    //line-height:51px;
    vertical-align: middle;
  }
}

.login-form {
  padding: 25px 25px 5px 25px;
  width: 493px;
  height: 445px;
  background: rgba(255, 255, 255, 1);
  opacity: 0.78;
  border-radius: 10px;

  .el-input {
    height: 38px;
    font-size: 16px;

    input {
      height: 38px;
    }
  }

  .input-icon {
    height: 39px;
    width: 14px;
    margin-left: 2px;
  }

  .loginButton {
    height: 38px;
    width: 100%;
    font-size: 18px;
    font-family: Source Han Sans SC;
    font-weight: 400;
    color: rgba(255, 255, 255, 1);
  }
}

.login-tip {
  font-size: 13px;
  text-align: center;
  color: #bfbfbf;
}

.login-code {
  width: 33%;
  display: inline-block;
  height: 38px;
  float: right;

  img {
    cursor: pointer;
    vertical-align: middle
  }
}
