export default {
    data() {
        return {
            title: '文件申请',
            editPage: true,
            id: '',
            ruleForm: {
                name: '',
                description: '',
                overtime: '',
                currentLimit: '',
                type: '',
                destinationAddress: '',
                latestGenerationTime: '',
                separator: '',
                fileFormat: '',
                serviceType: '2',
                status: '',
                dept: '',            
                belongUser: '',            
                fieldList: '',            
                belongApplication: '',            
                belongApplicationName: '',            
                sitPath: '',            
                prePath: '',            
                serviceParamList: [
                    {
                        name: '',
                        type: '',
                        parameterType: '', // 1入参 2出参
                        description: '',
                        defaultValue: '',
                        isPass: '',
                    }
                ],  
                applicationInfoList: [],
            },
            // 部门树
            deptTree: [],
            // 表单
            labelPosition: 'top',
            rules: {
                name: [
                    { required: true, message: '请输入名称', trigger: 'blur' },
                ],
                description: [
                    { required: true, message: '请输入描述', trigger: 'blur' },
                ],
                destinationAddress: [
                    { required: true, message: '请输入目标地址', trigger: 'blur' },
                ],
                latestGenerationTime: [
                    { required: true, message: '请选择时间', trigger: 'blur' },
                ],
                separator: [
                    { required: true, message: '请输入分隔符', trigger: 'blur' },
                ],
                fileFormat: [
                    { required: true, message: '请选择格式', trigger: 'blur' },
                ],
                status: [
                    { required: true, message: '请选择接口状态', trigger: 'blur' },
                ],
            },
        }
    },
    created() { 
        this.editPage = this.$route.name === '文件编辑' ? true : false
        if(this.editPage) {
            this.title = '文件编辑'
        }
        this.$api.findDeptTree({}).then(res => {
            console.log(res);
            this.deptTree = res.data
        })
    },
    mounted() {
        if(this.$route.name === '文件编辑') {
            this.id = this.$route.query.id ? this.$route.query.id : ''
            this.$api.viewServiceDetail({
                id: this.id
            }).then(res => {
                console.log(res);
                this.ruleForm = res.data
            })

        }
    },
    methods: {
        // 取消
        cancel() {
            console.log('取消');
            let currentView = this.$store.state.tagsView.visitedViews[0]
            for (currentView of this.$store.state.tagsView.visitedViews) {
                if (currentView.path === this.$route.path) {
                    break
                }
            }
            this.$store.dispatch('tagsView/delView', currentView).then(() => {
                this.$router.push({
                    path: '/dataServicePlatform/myService',
                })
            
            })
        },
        // 提交申请
        submitApplication() {
            console.log('提交申请',this.ruleForm);
            this.$refs.ruleForm.validate((valid) => {
                if (valid) {
                    this.$api.applyForService(this.ruleForm).then(res=>{
                        if(res.code==0 || res.code==200) {
                            this.$message.success(res.msg)
                            this.cancel()
                        } else {
                            this.$message.error(res.msg)
                        }
                    })
                } else {
                    return false;
                }
            })
        },
        // 确认更新
        confirmModify() {
            console.log('确定修改',this.ruleForm);
            this.$refs.ruleForm.validate((valid) => {
                if (valid) {
                    this.$api.serviceUpdate(this.ruleForm).then(res=>{
                        if(res.code==0 || res.code==200) {
                            this.$message.success(res.msg)
                            this.cancel()
                        } else {
                            this.$message.error(res.msg)
                        }
                    })
                } else {
                    return false;
                }
            })
        },
    }
}
