export default {
    data() {
        return {
            title: '应用申请',
            editPage: false,
            labelPosition: 'top',
            ruleForm: {
                appName: '',
                appCode: '',
                description: '',
                status: '',
                appKey: '',
                appSecret: '',
            },
            rules: {
                appName: [
                    { required: true, message: '请输入应用名称', trigger: 'blur' },
                ],
                appCode: [
                    { required: true, message: '请输入应用Code', trigger: 'blur' },
                ],
                description: [
                    { required: true, message: '请输入描述', trigger: 'blur' },
                ],
                appKey: [
                    { required: true, message: '请输入appKey', trigger: 'blur' },
                ],
                appSecret: [
                    { required: true, message: '请输入appSecret', trigger: 'blur' },
                ],
                status: [
                    { required: true, message: '请选择状态', trigger: 'change' },
                ],
            },
            appCode: '',
        }
    },
    created() { 
        this.editPage = this.$route.name === '应用编辑' ? true : false
        if(this.editPage) {
            this.title = '应用编辑'
        }
    },
    mounted() {
        this.appCode = this.$route.query.appCode ? this.$route.query.appCode : ''
        if(this.$route.name === '应用编辑') {
            this.$api.viewAppDetail({
                appCode: this.appCode
            }).then(res => {
                console.log(res,'yyyy');
                this.ruleForm = res.data
            })
        }
    },
    methods: {
        // 取消
        cancel() {
            console.log('取消');
            let currentView = this.$store.state.tagsView.visitedViews[0]
            for (currentView of this.$store.state.tagsView.visitedViews) {
                if (currentView.path === this.$route.path) {
                    break
                }
            }
            this.$store.dispatch('tagsView/delView', currentView).then(() => {
                this.$router.push({
                    path: '/dataServicePlatform/appManagement',
                })
            
            })
        },
        // 提交申请
        submitApplication() {
            console.log('提交申请');
            console.log(this.ruleForm);
            this.$refs.ruleForm.validate((valid) => {
                if (valid) {
                    this.$api.applyForApp(this.ruleForm).then(res=>{
                        if(res.code==0 || res.code==200) {
                            this.$message.success(res.msg)
                            this.cancel()
                        } else {
                            this.$message.error(res.msg)
                        }
                    })
                } else {
                    return false;
                }
            })
        },
        // 确认更新
        confirmModify() {
            console.log('确定修改');
            console.log(this.ruleForm);
            this.$refs.ruleForm.validate((valid) => {
                if (valid) {
                    this.$api.updateApp(this.ruleForm).then(res=>{
                        if(res.code==0 || res.code==200) {
                            this.$message.success(res.msg)
                            this.cancel()
                        } else {
                            this.$message.error(res.msg)
                        }
                    })
                } else {
                    return false;
                }
            })
        }
    }
}
