import crudIndIndicatorInfo from '../../../api/indicators/indIndicatorInfo'
import CRUD, {presenter, header, form, crud} from '@crud/crud'
import rrOperation from '@crud/RR.operation'
import crudOperation from '@crud/CRUD.operation'
import udOperation from '@crud/UD.operation'
import pagination from '@crud/Pagination'

// crud交由presenter持有
const defaultCrud = CRUD({
  title: '指标基本信息',
  url: crudIndIndicatorInfo.url,
  sort: 'id,desc',
  crudMethod: {...crudIndIndicatorInfo.method}
})
const defaultForm = {
  id: null,
  ieCode: null,
  ieName: null,
  categoryId: null,
  ieProp: null,
  ieType: null,
  ieDesc: null,
  ieRule: null,
  ieDefaultValue: null,
  ieMethod: null,
  calcFreq: null,
  status: null,
  ieDataUnit: null,
  ieLevel: null,
  startDate: null,
  endDate: null,
  retention: null,
  processInstanceId: null,
  manageDept: null,
  crtUserCode: null,
  crtOrgCode: null,
  crtDate: null,
  updUserCode: null,
  updOrgCode: null,
  updDate: null
}
export default {
  name: 'IndIndicatorInfo',
  components: {pagination, crudOperation, rrOperation, udOperation},
  mixins: [presenter(defaultCrud), header(), form(defaultForm), crud()],
  dicts: ['IE_PROP', 'IE_TYPE', 'IE_METHOD', 'CALC_FREQ', 'IE_STATUS', 'IE_DATA_UNIT', 'RETENTION'],
  data() {
    return {
      permission: {
        add: ['admin', 'indIndicatorInfo:add'],
        edit: ['admin', 'indIndicatorInfo:edit'],
        del: ['admin', 'indIndicatorInfo:del']
      },
      rules: {
        ieName: [
          {required: true, message: '指标名称不能为空', trigger: 'blur'}
        ],
        categoryId: [
          {required: true, message: '指标分类不能为空', trigger: 'blur'}
        ],
        ieProp: [
          {required: true, message: '指标类别不能为空', trigger: 'blur'}
        ],
        ieType: [
          {required: true, message: '指标类别不能为空', trigger: 'change'}
        ]
      }
    }
  },
  methods: {
    // 获取数据前设置好接口地址
    [CRUD.HOOK.beforeRefresh]() {
      return true
    }
  }
}
