export default {
    data() {
        return {
            title: 'API申请',
            reqParams: [
                {
                    name: '',
                    type: '',
                    parameterType: '1',
                    description: '',
                    defaultValue: '',
                    isPass: '',
                }
            ],
            resParams: [
                {
                    name: '',
                    type: '',
                    parameterType: '2',
                    description: '',
                    defaultValue: '',
                    isPass: '',
                }
            ],
            editPage: false,
            id: '',
            object: {
                name: '',
                description: '',
                overtime: '',
                currentLimit: '',
                type: '',
                destinationAddress: '',
                latestGenerationTime: '',
                separator: '',
                fileFormat: '',
                serviceType: '1',
                status: '',
                dept: '',            
                belongUser: '',            
                fieldList: '',            
                belongApplication: '',            
                belongApplicationName: '',            
                sitPath: '',            
                prePath: '',            
                serviceParamList: [
                    {
                        name: '',
                        type: '',
                        parameterType: '', // 1入参 2出参
                        description: '',
                        defaultValue: '',
                        isPass: '',
                    }
                ],  
                applicationInfoList: [],
            },
            // 应用列表
            appList: [],
            // 部门树
            deptTree: [],
        }
    },
    created() { 
        this.editPage = this.$route.name === 'API编辑' ? true : false
        if(this.editPage) {
            this.title = 'API编辑'
        }
        this.$api.findDeptTree({}).then(res => {
            console.log(res);
            this.deptTree = res.data
        })
        this.$api.qryOpenApp({}).then(res=>{
            this.appList = res.data
        })
    },
    mounted() {
        this.id = this.$route.query.id ? this.$route.query.id : ''
        if(this.$route.name === 'API编辑' || this.$route.name === 'API详情') {
            this.$api.viewServiceDetail({
                id: this.id
            }).then(res => {
                console.log(res);
                this.object = res.data
                this.reqParams = this.object.serviceParamList.filter(item=>{
                    return item.parameterType==='1'
                })
                this.resParams = this.object.serviceParamList.filter(item=>{
                    return item.parameterType==='2'
                })
            })
        }
    },
    methods: {
        change(val){
            console.log(val);
        },
        // 添加入参
        addReqParams() {
            this.reqParams.push({
                name: '',
                type: '',
                parameterType: '1',
                description: '',
                defaultValue: '',
                isPass: '',
            })
        },
        // 删除入参
        delReqParams(index) {
            this.reqParams.splice(index,1)
        },
        // 添加出参
        addResParams() {
            this.resParams.push({
                name: '',
                type: '',
                parameterType: '2',
                description: '',
                defaultValue: '',
                isPass: '',
            })
        },
        // 删除出参
        delResParams(index) {
            this.resParams.splice(index,1)
        },
        // 重置
        reset() {
            console.log('重置');
            this.object = {
                serviceType: '1',
            }
            this.reqParams = [
                {
                    name: '',
                    type: '',
                    parameterType: '1',
                    description: '',
                    defaultValue: '',
                    isPass: '',
                }
            ]
            this.resParams = [
                {
                    name: '',
                    type: '',
                    parameterType: '2',
                    description: '',
                    defaultValue: '',
                    isPass: '',
                }
            ]
        },
        // 提交申请
        submitApplication() {
            console.log('提交申请');
            this.object.serviceParamList = this.reqParams.concat(this.resParams)
            console.log(this.object);
            this.$api.applyForService(this.object).then(res=>{
                console.log(res);
                this.$message.success('申请成功!')
            })
        },
        // 确认更新
        confirmModify() {
            console.log('确定修改');
            this.object.serviceParamList = this.reqParams.concat(this.resParams)
            console.log(this.object);
            this.$api.serviceUpdate(this.object).then(res=>{
                console.log(res);
                this.$message.success('更新成功!')
            })
        }
    }
}
