.applyForApp {
  height: calc(100vh - 145px);
  padding: 16px;
  margin: 16px;
  background: #fff;
  border-radius: 8px;
  .contain {
    height: 100%;
    overflow: auto;
    .title {
      font-size: 16px;
      color: #79ac43;
      line-height: 22px;
      display: flex;
      align-items: center;
      i {
        width: 12px;
        height: 12px;
        border-radius: 2px;
        background: #79ac43;
        margin-right: 8px;
      }
    }
    // 基本信息
    .info {
      .content {
        margin-top: 8px;
        padding: 16px;
        background: #fafafa;
        font-size: 14px;
        color: #202224;
        .item {
          display: flex;
          flex-direction: column;
          margin-bottom: 16px;
          span {
            line-height: 20px;
            margin-bottom: 8px;
          }
        }
        .itemSpecial {
          display: flex;
          justify-content: space-between;
          p {
            width: 30%;
            display: flex;
            flex-direction: column;
            span {
              line-height: 20px;
              margin-bottom: 8px;
            }
          }
        }
      }
    }
    ::v-deep .el-input.is-disabled {
      background: #fff;
      border: none;
      .el-input__inner {
        background: #fff;
        border: none;
      }
    }
    ::v-deep .el-textarea.is-disabled {
      background: #fff;
      border: none;
      .el-textarea__inner {
        background: #fff;
        border: none;
      }
    }
  }
}
