export default {
    name: 'addUser',
    props: {
        rowId: {
            type: String,
            default: ''
        },
        addUserVisible: {
            type: Boolean,
            default: false
        },
    },
    data() {
        return {
            // 对话框标题
            title: '当前角色',
            name: '',
            // 树状图
            filterText: '',
            treeData: [],
            defaultProps: {
                id: 'id',
                children: 'children',
                label: 'name'
            },
            // 表格
            tableData: [],
            propList: [],
            // 分页
            pageSize: 9,
            total: 10,
            currentPage: 1,
            // tag标签
            dynamicTags: [],
            // 节点id
            nodeId: '',
            // 选中行
            currentRows: [],

        }
    },
    watch: {
        addUserVisible(val) {
            if (val) {
                this.initList()
                console.log(this.rowId,4333);
                this.dynamicTags = []
                // 查询已有菜单
                this.$api.queryUserByRole({roleId: this.rowId}).then(res => {
                    let data = []
                    res.data.forEach(item => {
                        data.push(item.username)
                    })
                    console.log(data);
                    data.forEach(item => {
                        this.tableData.forEach(row => {
                            if(item===row.username) {
                                this.$nextTick(() => {
                                    this.$refs.multipleTable.toggleRowSelection(row)
                                })
                            }
                        })
                    })
                })
            }
        },
        // 节点过滤
        filterText(val) {
            this.$refs.tree.filter(val);
        },
    },
    created() {
        this.$api.getSysDeptList({}).then(res => {
            this.treeData = res.content
        })
        
        this.propList = [
            {
                prop: 'username',
                label: '用户名'
            },
            {
                prop: 'nickName',
                label: '姓名'
            },
            {
                prop: 'email',
                label: '邮箱'
            },
            {
                prop: 'deptName',
                label: '部门'
            },
        ]
    },
    methods: {
        // 从第一页开始查询
        initList() {
            this.currentPage = 1
            this.queryList()
        },
        // 列表查询
        queryList() {
            let request = {
                deptId: this.nodeId,
                keyWord: this.name,
                size: this.pageSize,
                page: this.currentPage
            }
            console.log(request);
            this.$api.qryUserListPage(request).then(res => {
                this.tableData = res.data.records
                this.total = res.data.total
            })
        },
        // 关闭对话框
        closeAddUser() {
            this.$emit('closeAddUser')
        },
        // 确定
        confirmAddUser() {
            let ids = []
            this.currentRows.forEach(item => {
                ids.push(item.id)
            })
            console.log(ids, this.rowId,23333);
            this.$api.saveUserRoles({
                roleId: this.rowId,
                userIds: ids
            }).then(res => {
                if(res.code==200 || res.code==0) {
                    this.$message.success(res.msg)
                    this.$emit('confirmAddUser')
                }else {
                    this.$message.error(res.msg)
                }
            })
            
        },
        // 树状图过滤
        filterNode(value, data) {
            if (!value) return true;
            return data.name.indexOf(value.toString()) !== -1;
        },
        // 树状图节点点击
        handleNodeClick(node,data) {
            console.log(node);
            this.nodeId = node.id
            this.initList()
        },
        // 复选框
        handleSelectionChange(val) {
            console.log(val,1111111111111);
            this.currentRows = val
            this.dynamicTags = []
            val.forEach(item => {
                this.dynamicTags.push(item.username)
            });
        },
        // 分页
        handleCurrentChange(val) {
            console.log(val);
        },
        // tag标签关闭
        handleClose(tag) {
            this.dynamicTags.splice(this.dynamicTags.indexOf(tag), 1)
            this.tableData.forEach(row => {
                if(tag===row.username) {
                    this.$refs.multipleTable.toggleRowSelection(row)
                }
            })
        },
    },
}
