ul,
li {
  list-style: none;
}
// header
.portal-header {
  width: 100%;
  min-width: 1356px;
  height: 142px;
  display: flex;
  position: relative;
  background: url("../../../assets/bg_header.png") 100% 100% no-repeat;
  // background: linear-gradient(90deg, rgba(120, 171, 67, 1) 0%, rgba(136, 191, 77, 1) 100%);
  .portal-header-title {
    left: 155px;
    top: 19px;
    width: 105px;
    height: 33px;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: flex-start;
    margin-top: 19px;
    margin-left: 24px;
    .portal-header-title-top {
      left: 0px;
      top: 0px;
      width: 96px;
      height: 22px;
      display: flex;
      color: rgba(255, 255, 255, 1);
      font-size: 16px;
    }
    .portal-header-title-bottom {
      left: 0px;
      top: 22px;
      width: 105px;
      height: 11px;
      display: flex;
      color: rgba(255, 255, 255, 1);
      font-size: 8px;
      white-space: nowrap;
      transform: scale(0.65);
      margin-left: -18px;
    }
  }

  .portal-header-search {
    width: 401px;
    height: 32px;
    display: flex;
    justify-content: space-between;
    margin-top: 19px;
    margin-left: 223px;
  }
  .el-input--mini .el-input__inner {
    height: 32px;
  }
  .el-input--mini .el-input__icon {
    height: 28px;
  }
  .right-menu {
    display: flex;
    align-items: center;
    height: 70px;
    position: absolute;
    right: 24px;
    .profile-picture {
      width: 30px;
      height: 30px;
      border-radius: 30px;
      margin-right: 8px;
      display: flex;
      justify-content: center;
      align-items: center;
      color: #fff;
      font-weight: 700;
    }
  }
}
// container
.portal-container {
  width: 100%;
  min-width: 1356px;
  padding: 0 16px 16px 16px;
  display: flex;
  justify-content: flex-start;
  position: absolute;
  top: 86px;
  // aside
  // 隐藏滚动条
  .portal-aside::-webkit-scrollbar {
    display: none;
  }
  .portal-aside {
    width: 240px;
    height: calc(100vh - 105px);
    min-height: 626px;
    background: #fff;
    border-radius: 5px;
    padding: 8px;
    overflow-y: auto;
    margin-right: 16px;

    .aside-item {
      min-height: 114px;
      margin-bottom: 8px;
      background: rgba(246.568, 249.9, 244.90200000000002, 1);
      padding: 8px;
      .first {
        width: 100%;
        height: 20px;
        // padding: 8px;
        display: flex;
        justify-content: flex-start;
        img {
          width: 16px;
          height: 16px;
          line-height: 20px;
        }
        span {
          display: inline-block;
          margin-left: 5px;
          height: 20px;
          line-height: 20px;
          font-size: 14px;
          color: rgba(32, 34, 36, 1);
        }
      }
      .second {
        width: 100%;
        margin-top: 12px;
        // background: red;
        display: flex;
        justify-content: flex-start;
        flex-wrap: wrap;
        .second-item {
          width: 30%;
          margin-right: 3%;
          height: 17px;
          margin-bottom: 8px;
          color: rgba(153, 153, 153, 1);
          font-size: 12px;
          cursor: pointer;
        }
        .company-active,
        .number-active,
        .retail-active,
        .finance-active,
        .public-active {
          color: rgba(121, 172, 67, 1);
        }
      }
      .second-content {
        height: 42px;
        overflow: hidden;
      }
      .third {
        width: 100%;
        background: rgba(229, 240, 223, 1);
        display: flex;
        justify-content: flex-start;
        flex-wrap: wrap;
        padding: 2px;
        .third-item {
          width: 50%;
          font-size: 12px;
          color: #999;
          cursor: pointer;
          margin-bottom: 8px;
        }
      }
      .control {
        width: 100%;
        height: 16px;
        background: rgba(249.9, 249.9, 249.9, 1);
        cursor: pointer;
        text-align: center;
        margin-top: 8px;
        i {
          width: 16px;
          height: 16px;
          color: rgba(204, 204, 204, 1);
        }
      }
    }
  }
  // content
  .portal-content {
    width: calc(100vw - 288px);
    height: calc(100vh - 105px);
    // background: grey;
    min-width: 1076px;
    min-height: 626px;
    .content-top {
      width: 100%;
      height: 88px;
      margin-bottom: 8px;
      width: 100%;
      display: flex;
      justify-content: space-between;
      .top-item {
        width: 25%;
        height: 88px;
        background: #fff;
        border-radius: 5px;
        margin: 0 8px;
        display: flex;
        justify-content: space-between;
        cursor: pointer;
        .top-item-left {
          margin-left: 26px;
          margin-top: 16px;
          .name {
            color: rgba(128, 128, 128, 1);
            font-size: 14px;
            height: 20px;
            line-height: 20px;
            margin-bottom: 8px;
          }
          .count {
            color: rgba(56, 81, 105, 1);
            font-size: 24px;
            height: 28px;
            line-height: 28px;
          }
        }
        .top-item-right {
          width: 146px;
          height: 88px;
          border-radius: 0 0 5px 0;
        }
        .right1 {
          background: url("../../../assets/bg_table.png") 100% 100% no-repeat;
        }

        .right2 {
          background: url("../../../assets/bg_api.png") 100% 100% no-repeat;
        }

        .right3 {
          background: url("../../../assets/bg_index.png") 100% 100% no-repeat;
        }

        .right4 {
          background: url("../../../assets/bg_tag.png") 100% 100% no-repeat;
        }
      }
      .top-item-1 {
        margin-left: 0;
      }
      .top-item-4 {
        margin-right: 0;
      }
    }
    .content-middle {
      width: 100%;
      height: 90px;
      margin-bottom: 8px;
      padding: 14px 16px 13px 16px;
      background: #fff;
      display: flex;
      justify-content: space-between;
      align-items: center;
      border-radius: 5px;
      .middle-pre {
        width: 21px;
        height: 63px;
        background: rgba(249.9, 249.9, 249.9, 1);
        border-radius: 3px;
        cursor: pointer;
        z-index: 999;
        i {
          font-size: 14px;
          text-align: center;
          line-height: 63px;
          color: rgba(204, 204, 204, 1);
        }
      }
      .middle-dept {
        // width: calc(100% - 74px);
        // white-space: nowrap;
        // overflow-x: auto;
        flex: 1;
        overflow: hidden;
        .centerScroll {
          white-space: nowrap;
          .dept-item {
            width: 150px;
            height: 63px;
            display: inline-block;
            background: rgba(249.9, 249.9, 249.9, 1);
            margin-right: 16px;
            border-radius: 3px;
            padding: 8px;
            cursor: pointer;
            .dept-item-top {
              width: 100%;
              height: 20px;
              line-height: 20px;
              color: rgba(0, 0, 0, 0.5);
              font-size: 14px;
              display: flex;
              justify-content: flex-start;
              text-align: center;
              margin-bottom: 8px;
              .icon {
                width: 10px;
                height: 10px;
                background: rgba(
                  121.00032724875,
                  171.9975,
                  66.99302624999999,
                  1
                );
                margin-right: 5px;
                margin-top: 5px;
              }
              .popover-word {
                flex: 1;
                overflow: hidden;
                text-overflow: ellipsis;
                text-align: left;
              }
            }
            .dept-item-bottom {
              color: rgba(56, 81, 105, 1);
              font-size: 16px;
              height: 19px;
              span {
                color: rgba(56, 81, 105, 1);
                font-size: 12px;
                height: 17px;
                margin-left: 10px;
              }
            }
          }
          .dept-item:first-child {
            margin-left: 16px;
          }
        }
      }
      .middle-next {
        width: 21px;
        height: 63px;
        background: rgba(249.9, 249.9, 249.9, 1);
        border-radius: 3px;
        cursor: pointer;
        z-index: 999;
        i {
          font-size: 14px;
          text-align: center;
          line-height: 63px;
          color: rgba(204, 204, 204, 1);
        }
      }
    }
    .content-down {
      width: 100%;
      height: calc(100vh - 300px);
      min-height: 432px;
      display: flex;
      justify-content: space-between;
      .down-left {
        width: calc(100% - 678px);
        // height: 100%;
        height: calc(100vh - 300px);
        min-height: 432px;
        background: #fff;
        border-radius: 5px;
        padding: 20px 18px;
        .down-left-top {
          width: 100%;
          height: 50%;
          // height: 175px;
          .head {
            width: 100%;
            height: 20px;
            color: rgba(32, 34, 36, 1);
            font-size: 14px;
            line-height: 20px;
          }
          #asset-classification {
            width: 100%;
            height: calc(100vh - 700px);
            min-height: 140px;
          }
        }
        .down-left-bottom {
          width: 100%;
          height: 50%;
          // height: 175px;
          .head {
            width: 100%;
            height: 20px;
            color: rgba(32, 34, 36, 1);
            font-size: 14px;
            line-height: 20px;
            margin-bottom: 6px;
          }
          #business-trends {
            width: 100%;
            height: calc(100vh - 700px);
            min-height: 140px;
          }
        }
      }
      .down-middle {
        width: 331px;
        // height: 100%;
        height: calc(100vh - 300px);
        min-height: 432px;
        background: #fff;
        border-radius: 5px;
        padding: 10px 16px 21px 16px;
        .head {
          width: 100%;
          height: 20px;
          color: rgba(32, 34, 36, 1);
          font-size: 14px;
          line-height: 20px;
        }
        .scale {
          width: 100%;
          height: 89px;
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-top: 16px;
          .scale-item {
            width: 93px;
            height: 89px;
            background: rgba(247, 250, 245, 1);
            // padding: 16px;
            padding: 24px 12px;
            text-align: center;
            .name {
              // height: 17px;
              line-height: 17px;
              color: rgba(121, 172, 67, 1);
              font-size: 12px;
              margin-bottom: 7px;
            }
            .count {
              height: 17px;
              line-height: 17px;
              color: rgba(56, 81, 105, 1);
              font-size: 12px;
              span {
                color: rgba(121, 172, 67, 1);
              }
            }
          }
        }
        #organization-scale-ranking {
          width: 100%;
          // height: 232px;
          height: calc(100vh - 500px);
          min-height: 232px;
          margin-top: 24px;
        }
      }
      .down-right {
        width: 331px;
        // height: 100%;
        height: calc(100vh - 300px);
        min-height: 432px;
        .down-right-top {
          width: 100%;
          // height: 258px;
          height: calc(100vh - 433px);
          min-height: 298px;
          padding: 10px 13px;
          border-radius: 5px;
          background: #fff;
          margin-bottom: 8px;
          .head {
            width: 100%;
            height: 20px;
            color: rgba(32, 34, 36, 1);
            font-size: 14px;
            line-height: 20px;
            margin-bottom: 6px;
          }
          #data-asset-growth {
            width: 100%;
            height: calc(100vh - 500px);
            min-height: 231px;
          }
        }
        .down-right-bottom {
          width: 100%;
          height: 126px;
          padding: 10px 13px;
          border-radius: 5px;
          background: #fff;
          .head {
            width: 100%;
            height: 20px;
            color: rgba(32, 34, 36, 1);
            font-size: 14px;
            line-height: 20px;
            margin-bottom: 6px;
          }
          .asset {
            width: 100%;
            height: 64px;
            display: flex;
            justify-content: space-between;
            align-items: center;
            .asset-item {
              width: 70px;
              height: 64px;
              background: rgba(247, 250, 245, 1);
              padding: 10px;
              font-size: 12px;
              color: rgba(121, 172, 67, 1);
              text-align: center;
              cursor: pointer;
              .name {
                height: 17px;
                line-height: 17px;
                margin-bottom: 7px;
              }
              .count {
                color: rgba(56, 81, 105, 1);
                span {
                  color: rgba(121, 172, 67, 1);
                  margin-left: 2px;
                }
              }
            }
          }
        }
      }
    }
  }
}
