export default {
  data() {
    return {
      title: "API详情",
      reqParams: [
        {
          name: "",
          type: "",
          parameterType: "1",
          description: "",
          defaultValue: "",
          isPass: "",
        },
      ],
      resParams: [
        {
          name: "",
          type: "",
          parameterType: "2",
          description: "",
          defaultValue: "",
          isPass: "",
        },
      ],
      id: "",
      ruleForm: {
        name: "",
        description: "",
        overtime: "",
        currentLimit: "",
        type: "",
        destinationAddress: "",
        latestGenerationTime: "",
        separator: "",
        fileFormat: "",
        serviceType: "1",
        status: "",
        dept: "",
        belongUser: "",
        dockName: "",
        fieldList: "",
        belongApplication: "",
        belongApplicationName: "",
        sitPath: "",
        prePath: "",
        serviceParamList: [
          {
            name: "",
            type: "",
            parameterType: "", // 1入参 2出参
            description: "",
            defaultValue: "",
            isPass: "",
          },
        ],
        applicationInfoList: [],
      },
      // 应用列表
      appList: [],
      // 部门树
      deptTree: [],
    };
  },
  mounted() {
    this.initData()
  },
  // beforeRouteLeave(to, from, next) {
  //   if (to.name === "myService") {
  //     this.$store.commit("SET_KEEPALIVE", true);
  //   } else {
  //     this.$store.commit("SET_KEEPALIVE", false);
  //   }
  //   next();
  // },
  // watch: {
  //   $route(e) {
  //     if (e.name === "apiDetail") {
  //       this.initData();
  //     }
  //   }
  // },
  methods: {
    initData() {
      this.$api.qryOpenApp({}).then((res) => {
        this.appList = res.data;
      });
      this.$api.findDeptTree({}).then((res) => {
        this.deptTree = res.data;
      });
      this.id = this.$route.query.id ? this.$route.query.id : "";
      this.$api
        .viewServiceDetail({
          id: this.id,
        })
        .then((res) => {
          this.ruleForm = res.data;
          this.reqParams = this.ruleForm.serviceParamList?.filter((item) => {
            return item.parameterType === "1";
          });
          this.resParams = this.ruleForm.serviceParamList?.filter((item) => {
            return item.parameterType === "2";
          });
        });
    },
  },
};
