        //     p {
        //         width: 30%;
        //         display: flex;
        //         flex-direction: column;
        //         justify-content: space-between;
        //         .el-date-editor {
        //             width: 100%;
        //         }
        //         span {
        //             line-height: 20px;
        //             margin-bottom: 8px;
        //         }
        //     }
        // }
        // 表单
        ::v-deep .el-form {
          .el-form-item__label {
            font-weight: 400;
            color: #202224;
          }
          .el-form-item__content {
            .el-input {
              width: 100%;
            }
            .el-select {
              width: 100%;
            }
            .el-date-editor {
              width: 100%;
            }
            .el-cascader {
              width: 100%;
            }
          }
        }
      }
    }
    .btn {
      margin-top: 70px;
      display: flex;
      justify-content: flex-end;
      .el-button {
        width: 86px;
        height: 30px;
        margin-left: 14px;
        background: #79ac43;
        color: #fff;
        border: none;
        border-radius: 4px;
      }
      .cancelBtn {
        background: #fff;
        border: 1px solid #79ac43;
        color: #79ac43;
      }
    }
  }
}
