/deep/.el-table__body-wrapper::-webkit-scrollbar {
  height: 10px !important;
}
.dataErrorMsg {
  padding:60px 0px;
  text-align: center;
}
.myCollection {
  height: calc(100vh - 135px);
  background: #fff;
  margin: 0 16px 16px 16px;
  padding: 16px;
  border-radius: 8px;
  overflow: hidden;
  // tab
  ::v-deep .el-tabs {
    height: auto;

    .el-tabs__header {
      margin: 0;

      .el-tabs__nav-wrap {
        .el-tabs__nav-scroll {
          height: 25px;

          .el-tabs__nav {
            height: 100%;

            .el-tabs__item {
              height: 100%;
              line-height: 25px;
              font-size: 14px;
              color: #202224;
            }

            .el-tabs__item.is-active {
              color: #79ac43;
            }
          }
        }
      }

      .el-tabs__nav-wrap::after {
        background: transparent;
      }
    }
  }

  // 列表部分
  .list {
    display: flex;
    flex-wrap: wrap;
    // justify-content: space-between;
    align-content: flex-start;
    height: calc(100% - 108px);
    overflow: auto;
    margin-top: 24px;

    .listItem {
      width: 32%;
      // height: 32%;
      margin-bottom: 8px;
      padding: 12px;
      border: 1px solid #e8e8e8;
      border-radius: 4px;
      margin-right: 1%;

      .title {
                // height: 20px;
                display: flex;
                justify-content: space-between;
                align-items: center;
                font-size: 14px;
                color: #202224;
                margin-bottom: 8px;
                width: 100%;
                min-height: 32px;

                .title-left {
                    display: flex;
                    width: calc(100% - 14px);
                    align-items: center;
                    .title-left-tag {
                        display: inline-table;
                    }
                    .el-tag {
                        margin-right: 8px;
                        color: #409eff;
                        background-color: #ecf5ff;
                        border: 1px solid #d9ecff;
                    }   
                    .el-tag.el-tag--success {
                        background-color: #f2f7ec;
                        border-color: #e4eed9;
                        color: #79ac43;
                    }

                    .el-tag.el-tag--warning {
                        background-color: #fff8e6;
                        border-color: #fff1cc;
                        color: #ffba00;
                    }
                    .el-tag.el-tag--danger {
                        background-color: #dbe9f8;
                        border: 1px solid #d0e5fa;
                        color: #0062ff;
                    }
                    div {
                        padding-left: 5px;
                        white-space: nowrap;
                        overflow: hidden;
                        text-overflow: ellipsis;
                    }
                    .table-title {}
                }
        span {
                    white-space: nowrap;
                    overflow: hidden;
                    text-overflow: ellipsis;
          .el-tag {
            margin-right: 8px;
            color: #409eff;
            background-color: #ecf5ff;
            border: 1px solid #d9ecff;
          }

          .el-tag.el-tag--success {
            background-color: #f2f7ec;
            border-color: #e4eed9;
            color: #79ac43;
          }

          .el-tag.el-tag--warning {
            background-color: #fff8e6;
            border-color: #fff1cc;
            color: #ffba00;
          }
        }

        i {
          cursor: pointer;
        }

        .el-icon-star-on {
          color: #f7ba2a;
          font-size: 18px;
        }
      }

      .detail {
        font-size: 12px;

        p {
          height: 20px;
          line-height: 20px;
          background: #fafafa;
          margin: 0 0 4px 0;
          width: 100%;

          display: flex;
          justify-content: space-between;

          span {
            width: 100%;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
            color: #808080;
          }
        }

        .btn {
          width: 100%;
          height: 20px;
          margin-top: 10px;
          display: flex;
          align-items: center;

          div {
            width: 100%;
            text-align: center;
          }

          .line {
            color: #e6ebf5;
          }
        }
      }
    }
  }

  // 分页
  .pagination {
    margin-top: 24px;
    height: 32px;
    display: flex;

    flex-direction: row-reverse;

    .el-pagination {
      margin: 0;
    }
  }

  // 弹窗
  ::v-deep .el-dialog__wrapper.preView {
    // 数据预览弹窗
    .el-dialog {
      // height: 308px;
      border-radius: 8px;
      // overflow: hidden;
      box-sizing: border-box;
      padding: 16px 24px;

      .el-dialog__header {
        padding: 0;

        .el-dialog__title {
          font-size: 16px !important;
          color: #202224 !important;
        }
      }

      .el-dialog__body {
        padding: 0;
        margin-top: 18px;

        // .el-table::before {
        //   display: none;
        // }

        // .el-table {
        //   border: 1px solid #e8e8e8;
        //   // header
        //   .el-table__header-wrapper {
        //     .el-table__header {
        //       tr {
        //         border: 1px solid #e8e8e8;

        //         th {
        //           color: rgba(32, 34, 36, 1);
        //           font-size: 14px;
        //           font-weight: 400;
        //           border: none;
        //           background: #fafafa;
        //         }
        //       }
        //     }
        //   }

        //   // body
        //   .el-table__body-wrapper {
        //     .el-table__body {
        //       tr {
        //         font-size: 12px;
        //         color: #4d4e50;
        //       }
        //     }
        //   }

        //   .cell {
        //     // white-space: nowrap;
        //     // overflow: hidden;
        //     // text-overflow: ellipsis;
        //   }
        // }
      }
    }
  }

  // 申请权限
  ::v-deep .el-dialog__wrapper.authority {
    .el-dialog {
      padding: 16px;
      height: 88px;
      box-sizing: border-box;
      border-radius: 8px;

      .el-dialog__header {
        padding: 0;
        display: none;
      }

      .el-dialog__body {
        padding: 0;
        display: flex;
        justify-content: space-between;

        .el-button {
          width: 104px;
          height: 56px;
          border: 1px solid #79ac43;
          color: #79ac43;
          font-size: 14px;
          margin: 0;
        }
      }
    }
  }

  // 直接申请
  ::v-deep .el-dialog__wrapper.directApply {
    .el-dialog {
      height: 292px;
      padding: 16px 24px;
      box-sizing: border-box;
      border-radius: 8px;

      .el-dialog__header {
        padding: 0;

        .el-dialog__title {
          font-size: 16px !important;
          color: #202224;
        }
      }

      .el-dialog__body {
        padding: 0;
        margin-top: 26px;

        p {
          width: 592px;
          height: 64px;
          margin: 0 0 16px 0;
          padding: 20px 24px;
          border: 1px solid #e7e7e7;
          border-radius: 4px;
          box-sizing: border-box;
          display: flex;
          justify-content: space-between;
          align-items: center;

          .el-date-editor {
            border: none;
            padding-right: 0;
            width: 240px;
          }
        }

        .active {
          border: 1px solid #79ac43;
          background: #e8f2dc;

          .el-date-editor {
            background: #e8f2dc;
            color: #79ac43;

            .el-range-input {
              background: #e8f2dc;
              color: #79ac43;
            }

            .el-range-separator {
              color: #79ac43;
            }
          }
        }
      }

      .el-dialog__footer {
        padding: 0;

        .el-button {
          width: 88px;
          height: 36px;
          border: 1px solid #79ac43;
          color: #79ac43;
          font-size: 14px;
          margin: 0;
        }

        .el-button--primary {
          color: #fff;
        }
      }
    }
  }

  // 提示弹窗
  ::v-deep .el-dialog__wrapper.tipDialog {
    .el-dialog {
      border-radius: 8px;
      padding: 16px;
      box-sizing: border-box;

      .el-dialog__header {
        padding: 0;
        display: none;
      }

      .el-dialog__body {
        height: 22px;
        line-height: 22px;
        padding: 0;
        font-size: 14px;
        display: flex;
        align-items: center;

        i {
          color: #79ac43;
          font-size: 16px;
          margin-right: 8px;
        }

        span {
          color: rgba(0, 0, 0, 0.9);
        }
      }
    }
  }
}
