.panelGroup {
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    // 搜索框
    .search {
        display: flex;
        .el-autocomplete {
            .el-input  {
                width: 515px;
                height: 40px;
                .el-input__inner {
                    height: 100%;
                    border-top-left-radius: 20px;
                    border-bottom-left-radius: 20px;
                }
                .el-input-group__append {
                    border: none;
                    height: 32px;
                    .el-button {
                        width: 75px;
                        height: 40px;
                        border-top-right-radius: 20px;
                        border-bottom-right-radius: 20px;
                        font-size: 14px;
                        background: #84C22E;
                        color: #fff;
                    }
                }
            }
        }
    }

    // 按钮
    .btnList {
        margin-top: 24px;
        display: flex;
        justify-content: space-around;
        width: 100%;
        .el-link {
            width: 20%;
            height: 100px;
            border-radius: 16px;
            background: #fff;
            padding: 14px 20px;
            .el-link--inner {
                width: 100%;
                display: flex;
                justify-content: space-between;
                align-items: center;
                .icon {
                    width: 50px;
                    height: 50px;
                    background: rgba(0,0,0,0.04);
                    margin-right: 8px;

                }
                .desc {
                    flex: 1;
                    p {
                        margin: 8px 0;
                    }
                    span {
                        font-weight: 700;
                    }
                }
            }
        }
    }
    // 图表部分
    .details {
        display: flex;
        .chart {
            flex: 1;
            display: flex;
            justify-content: space-between;
            flex-wrap: wrap;
            .chartDom {
                width: 49%;
                height: 500px;
                border-radius: 16px;
                box-shadow: 0 0 10px #ccc;
                margin-bottom: 16px;
                .title {
                    font-family: '黑体';
                    font-size: 20px;
                    font-weight: 700;
                    margin-top: 18px;
                    padding: 0 20px;
                    color: rgba(0,0,0,0.8);
                    display: flex;
                    justify-content: space-between;
                }
                .des {
                    display: flex;
                    justify-content: space-around;
                    margin-top: 16px;
                    .box {
                        display: flex;
                        flex-direction: column;
                        color: rgb(0, 187, 255);
                        align-items: center;
                        justify-content: center;
                        background: rgba(0,0,0,0.04);
                        padding: 16px;
                        border-radius: 8px;
                    }
                }
                #barChart {
                    width: 100%;
                    height: 350px;
                }
            }
            .lineChart {
                width: 58%;
            }
            .pieChart {
                width: 40%;
            }
        }
        .board {
            width: 200px;
            padding: 8px;
            background: #fff;
            border-radius: 8px;
            margin-left: 8px;
            .title {
                font-family: '黑体';
                font-size: 20px;
                font-weight: 700;
                color: rgba(0,0,0,0.8);
                margin: 8px 0;
            }
            .item {
                margin-top: 8px;
                padding: 8px;
                border-radius: 8px;
                box-shadow: 0 0 10px #ccc;
                display: flex;
                align-items: center;
                .word {
                    width: 90%;
                    p {
                        display: flex;
                        .el-progress {
                            width: 80%;
                            margin-left: 3px;
                            .el-progress-bar {
                                width: 100%;
                            }
                            .el-progress__text {
                                width: 0;
                                display: none;
                            }
                        }
                    }
                }
            }
        }
    }
}
