.myShopCar {
  width: calc(100% - 32px);
  // height: 100%;
  height: calc(100vh - 135px);
  margin: 0 16px 16px 16px;
  padding: 12px;
  box-sizing: border-box;
  background: #fff;
  display: flex;
  flex-direction: column;
  .condition {
    display: flex;
    justify-content: space-between;
    align-items: center;
    .left {
      width: 80%;
      span {
        font-size: 14px;
        color: #202224;
      }
      .el-input {
        width: 180px;
        margin-left: 8px;
      }
      .el-button {
        border-radius: 4px;
        color: #202224;
      }
    }
    .batchOperation {
      width: 86px;
      height: 30px;
      border-radius: 2px;
      background: #5783f2;
      color: #fff;
    }
    .batchOperation.is-disabled {
      opacity: 0.7;
    }
  }
  // 表格
  .table {
    flex: 1;
    overflow: auto;
  }
  .el-table::before {
    background-color: #fff;
    height: 0;
  }
  ::v-deep .el-table {
    margin-top: 24px;
    padding-left: 4px;
    // header
    .el-table__header-wrapper {
      .el-table__header {
        tr {
          th {
            color: rgba(32, 34, 36, 1);
            font-size: 14px;
            font-weight: 400;
            border: none;
          }
        }
      }
    }
    // body
    .el-table__body-wrapper {
      .el-table__body {
        border-spacing: 0 8px;
        tr {
          td:first-child {
            border-left: 1px solid rgba(232, 232, 232, 1);
          }
          td:last-child {
            position: relative;
            right: 4px;
            border-right: 1px solid rgba(232, 232, 232, 1);
          }
          td {
            color: rgba(77, 78, 80, 1);
            border-top: 1px solid rgba(232, 232, 232, 1);
            border-bottom: 1px solid rgba(232, 232, 232, 1);
          }
        }
      }
    }
    // 复选框
    .cell {
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
      .el-checkbox {
        width: 16px;
        height: 16px;
        .el-checkbox__input {
          .el-checkbox__inner {
            width: 16px;
            height: 16px;
          }
        }
        .el-checkbox__input.is-checked,
        .el-checkbox__input.is-indeterminate {
          width: 16px;
          height: 16px;
          display: flex;
          justify-content: center;
          align-items: center;
          background: #fff;
          border: 1px solid #79ac43;
          border-radius: 2px;
        }
        .el-checkbox__input.is-indeterminate {
          .el-checkbox__inner {
            width: 8px;
            height: 4px;
            background: #79ac43;
          }
          .el-checkbox__inner::before {
            display: none;
          }
        }
        .el-checkbox__input.is-checked {
          .el-checkbox__inner {
            width: 8px;
            height: 8px;
            background: #79ac43;
          }
          .el-checkbox__inner::after {
            display: none;
          }
        }
      }
    }
  }
  // 分页
  .pagination {
    height: 64px;
    display: flex;
    align-items: center;
    flex-direction: row-reverse;
    .el-pagination {
      margin: 0;
    }
  }
  // 弹窗
  ::v-deep .el-dialog__wrapper {
    .el-dialog {
      border-radius: 8px;
      padding: 16px;
      box-sizing: border-box;
      .el-dialog__header {
        padding: 0;
        display: none;
      }
      .el-dialog__body {
        height: 22px;
        line-height: 22px;
        padding: 0;
        font-size: 14px;
        display: flex;
        align-items: center;
        i {
          color: #79ac43;
          font-size: 16px;
          margin-right: 8px;
        }
        span {
          color: rgba(0, 0, 0, 0.9);
        }
      }
      .el-dialog__footer {
        width: 100%;
        display: flex;
        padding: 0;
        margin-top: 8px;
        .el-button {
          width: 84px;
          height: 30px;
          margin: 0;
          border-radius: 4px;
        }
        .el-button--default {
          border: none;
          background: #e5e5e5;
          color: #202224;
        }
        .el-button--primary {
          background: #79ac43;
        }
      }
    }
  }
}
