import { mixinsFunctions } from "@/mixins/mixinsFunc";
export default {
  mixins: [mixinsFunctions],
  data() {
    return {
      // basicinfo
      basicinfo: {
        id: "",
        targetName: "",
        targetType: "",
        classifyOne: "",
        classifyTwo: "",
        businessDept: "",
        targetLevel: "",
        targetDefinition: "",
        targetRule: "",
        currency: "",
        myCrtTime: "",
        myUpdTime: "",
        dimension1: "",
        dimensionValue1: "",
        dimension2: "",
        dimensionValue2: "",
        catalogue1: "",
        catalogue2: "",
        catalogue1Name: "",
        catalogue2Name: "",
        statisticsRate: "",
        superviseAlert: "",
        measureUnit: "",
        dataType: "",
        referenceBasis: "",
        processSystem: "",
        safetyLevel: "",
        valueAttribute: "",
        currentValue: '', //当期值
        contemporaneousValue: '', //同期值
        yearOnYearIncrement: '', //同比增量
        yearOnYearGrowth: '', //同比增幅
        previousPeriodValue: '', //上期值
        monthOnMonthIncreament: '', //环比增量
        monthOnMonthGrowth: '', //环比增幅
        initialValueOfTheYear: '', //年初值
        yearOnYearIncre: '', //较年初增量
        increaseOverTheBeginningOfTheYear: '',//较年初增幅
      },
      detailsView: false,
      id: "",
      assetsState: "",
      // 目录树
      catalogueList: [],
      cataValue: [],
      basicInfoProp: [
        {
          prop: "targetName",
          label: "指标名称",
          align: "center",
        },
        {
          prop: "targetCode",
          label: "指标编码",
          align: "center",
        },
        {
          prop: "targetType",
          label: "指标类型",
          align: "center",
        },
        {
          prop: "classifyOne",
          label: "指标一级分类",
          align: "center",
        },
        {
          prop: "classifyTwo",
          label: "指标二级分类",
          align: "center",
        },
        {
          prop: "deptName",
          label: "业务属主部门",
          align: "center",
        },
        {
          prop: "targetLevel",
          label: "指标层级",
          align: "center",
        },
        {
          prop: "targetDefinition",
          label: "指标定义",
          align: "center",
        },
        {
          prop: "targetRule",
          label: "指标口径规则",
          align: "center",
        },
        {
          prop: "currency",
          label: "币种",
          align: "center",
        },
        {
          prop: "myCrtTime",
          label: "创建时间",
          align: "center",
        },
        {
          prop: "myUpdTime",
          label: "更新时间",
          align: "center",
        },
      ],
      basicInfoDetailProp: [
        {
          prop: "statisticsRate",
          label: "指标生成频率",
          align: "center",
        },
        {
          prop: "superviseAlert",
          label: "监管属性",
          align: "center",
        },
        {
          prop: "measureUnit",
          label: "度量单位",
          align: "center",
        },
        {
          prop: "dataType",
          label: "数据类型",
          align: "center",
        },
        {
          prop: "referenceBasis",
          label: "参考依据",
          align: "center",
        },
        {
          prop: "processSystem",
          label: "权威加工系统",
          align: "center",
        },
        {
          prop: "safetyLevel",
          label: "指标安全等级",
          align: "center",
        },
        {
          prop: "valueAttribute",
          label: "指标数值属性",
          align: "center",
        },
      ],
      dimensionList: [],
      basicInfoList: [],
      targetBgImg: require("../../../assets/targetBg.png"),
    };
  },
  methods: {
    initData() {
      if (this.$route.name === "指标详情") {
        this.detailsView = true;
      }
      // 目录树
      this.$api.getAllCatalogueList({}).then((res) => {
        this.catalogueList = res.data;
      });
      // 数据
      this.id = this.$route.query.id; // 通过路由传递id
      this.$api
        .getTargetById({
          id: this.id,
          isRecord: this.detailsView,
        })
        .then((res) => {
          this.basicinfo = res;
          this.cataValue = [];
          this.basicInfoDetailList = [];
          this.dimensionList = [];
          this.basicInfoList = [];
          // 发布状态
          this.assetsState = this.basicinfo.assetsState;
          this.basicInfoList.push(this.basicinfo || []);
          // 返回的目录  -- 渲染
          let temp = this.basicinfo.catalogues;
          if(this.basicinfo.dimension1) {
            this.dimensionList.push(
              {
                name: this.basicinfo.dimension1,
                value: this.basicinfo.dimensionValue1,
              }
            )
          }
          if(this.basicinfo.dimension2) {
            this.dimensionList.push(
              {
                name: this.basicinfo.dimension2,
                value: this.basicinfo.dimensionValue2,
              }
            )
          }
          for (let key in temp) {
            key = key.split("/");
            this.cataValue.push({ id: key });
          }
        });
    },
    confirmModify() {
      let list = JSON.parse(JSON.stringify(this.cataValue));
      list = list.map((item) => {
        return (item = item.id.join("/"));
      });

      let listSet = Array.from(new Set(list));
      if (listSet.length !== list.length) {
        this.$message.error("添加目录不能重复");
      } else {
        this.$api
          .updateAssetTarget({
            id: this.id,
            list: list,
            assetsState: this.assetsState,
          })
          .then((res) => {
            this.$message.success("修改成功!");
            this.closeThisPage();
          });
      }
    },
    handleChange(value) {
      console.log(value);
    },
    deleteCatalogue(index) {
      this.cataValue.splice(index, 1);
    },
    addCatalogue() {
      if (this.cataValue.length > 4) {
        this.$message.warning("最多添加5条目录");
      } else {
        this.cataValue.push({
          id: [],
        });
      }
    },
  },
};
