import { mapGetters, mapState } from "vuex";
import Breadcrumb from "@eladmin/components/Breadcrumb";
import Hamburger from "@eladmin/components/Hamburger";
// import Github from '@/components/Github'
import Screenfull from "@eladmin/components/Screenfull";
import SizeSelect from "@eladmin/components/SizeSelect";
import Search from "@eladmin/components/HeaderSearch";
import Avatar from "@eladmin/assets/images/avatar.png";
import RoleSelect from "@eladmin/components/RoleSelect";
import ThemeSelect from "../../components/ThemeSelect";
import Logo from "../components/Sidebar/Logo";

export default {
  components: {
    Breadcrumb,
    Hamburger,
    Screenfull,
    SizeSelect,
    Search,
    // Github
    RoleSelect,
    ThemeSelect,
    Logo,
  },
  data() {
    return {
      Avatar: Avatar,
      dialogVisible: false,
      searchValue: "", // 搜索内容
    };
  },
  created() {
    // 首页搜索保持搜索内容在输入框
    this.searchValue = this.$route.query.data
  },
  computed: {
    ...mapGetters(["sidebar", "device", "user", "baseApi"]),
    ...mapState({
      showBreadcrumb: (state) => state.settings.showBreadcrumb,
    }),
    showLogo() {
      return this.$store.state.settings.sidebarLogo;
    },
    show: {
      get() {
        return this.$store.state.settings.showSettings;
      },
      set(val) {
        this.$store.dispatch("settings/changeSetting", {
          key: "showSettings",
          value: val,
        });
      },
    },
  },
  methods: {
    toggleSideBar() {
      this.$store.dispatch("app/toggleSideBar");
    },
    inputChange(value) {
      this.searchValue = value
      localStorage.setItem('searchKeyWords',value)
    },
    globalSearch() {
      console.log('搜索')
      this.$router.push({
        path: "/searchPage/index",
        query: {
          data: this.searchValue,
          from: 'other'
        },
      });
    },
    open() {
      this.$confirm("确定注销并退出系统吗？", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning",
      }).then(() => {
        this.logout();
      });
    },
    logout() {
      this.$store.dispatch("LogOut").then(() => {
        location.reload();
      });
    },
  },
};
