import request from '../utils/request'

export function login(username, password, code, uuid,loginType) {
  const data = {
    username,
    password,
    uuid,
    loginType
  }
  if (code) {
    data.code = code
  }
  return request({
    url: 'auth/login',
    method: 'post',
    data: data
  })
}
export function ssoLogin(token,userName,targetUrl) {
  return request({
    url: 'sso/login',
    method: 'post',
    data: {
      token,
      userName,
      targetUrl
    }
  })
}

export function getInfo() {
  return request({
    url: 'auth/info',
    method: 'get'
  })
}

export function getCodeImg() {
  return request({
    url: 'auth/code',
    method: 'get'
  })
}

export function logout() {
  return request({
    url: 'auth/logout',
    method: 'delete'
  })
}
