import request from '../../utils/request'

export function getErrDetail(id) {
  return request({
    url: 'api/logs/error/' + id,
    method: 'get'
  })
}

export function delAllError() {
  return request({
    url: 'api/logs/del/error',
    method: 'delete'
  })
}

export function delAllInfo() {
  return request({
    url: 'api/logs/del/info',
    method: 'delete'
  })
}
export function getLogStatistics() {
  return request({
    url: 'api/logs/logStatistics',
    method: 'get'
  })
}

export function getlogRanking() {
  return request({
    url: 'api/logs/logRanking',
    method: 'get'
  })
}

export function getformRanking() {
  return request({
    url: 'api/logs/formRanking',
    method: 'get'
  })
}
