<template>
  <el-menu-item v-if="!menu.hidden && !menu.children && menu.meta" :index="index">
    <router-link v-if="menu.meta" :to="{path: menu.path}">
      {{
        menu.meta.title
      }}
    </router-link>
  </el-menu-item>
  <el-submenu v-else-if="menu.meta && !menu.hidden" :index="index">
    <template slot="title">{{ menu.meta.title }}</template>
    <menu-item v-for="route in menu.children" :menu="route" />
  </el-submenu>
</template>

<script>
export default {
  name: 'MenuItem',
  props: {
    menu: { type: Object, default: {}},
    index: { type: [Number, String], default: 1 }
  }
}
</script>

