<template>
  <div class="icons-container">
    <aside>
      <a href="https://panjiachen.github.io/vue-element-admin-site/guide/advanced/icon.html" target="_blank">Add and use
      </a>
    </aside>
    <el-tabs type="border-card">
      <el-tab-pane label="SVG Icons">
        <div v-for="item of svgIcons" :key="item" @click="handleClipboard(generateIconCode(item),$event)">
          <el-tooltip placement="top">
            <div slot="content">
              {{ generateIconCode(item) }}
            </div>
            <div class="icon-item">
              <svg-icon :icon-class="item" class-name="disabled" />
              <span>{{ item }}</span>
            </div>
          </el-tooltip>
        </div>
      </el-tab-pane>
      <el-tab-pane label="Element-UI Icons">
        <div v-for="item of elementIcons" :key="item" @click="handleClipboard(generateElementIconCode(item),$event)">
          <el-tooltip placement="top">
            <div slot="content">
              {{ generateElementIconCode(item) }}
            </div>
            <div class="icon-item">
              <i :class="'el-icon-' + item" />
              <span>{{ item }}</span>
            </div>
          </el-tooltip>
        </div>
      </el-tab-pane>
      <el-tab-pane label="Iconfont Icons">
        <div v-for="item of iconfontIcons.glyphs" @click="handleClipboard(generateIconfontIconCode(item),$event)">
          <el-tooltip placement="top">
            <div slot="content">
              {{ generateIconfontIconCode(item) }}
            </div>
            <div class="icon-item">
              <i :class="generateIconfontIconClass(item)" style="font-size: 25px" />
              <span>{{ item['name'] }}</span>
            </div>
          </el-tooltip>
        </div>
      </el-tab-pane>
    </el-tabs>
  </div>
</template>

<script>
  import clipboard from '../../../utils/clipboard'
  import svgIcons from './svg-icons'
  import elementIcons from './element-icons'
  import iconfonts from './iconfont-icons'

  export default {
    name: 'Icons',
    data() {
      return {
        svgIcons,
        elementIcons
      }
    },
    computed: {
      iconfontIcons() {
        return iconfonts
      }
    },
    mounted() {
      console.log('============', iconfonts)
    },
    methods: {
      generateIconCode(symbol) {
        return `<svg-icon icon-class="${symbol}" />`
      },
      generateElementIconCode(symbol) {
        return `<i class="el-icon-${symbol}" />`
      },
      generateIconfontIconCode(symbol) {
        // console.log('--------------------', symbol)
        return `<i class="icon ${iconfonts['font_family']} ${iconfonts['css_prefix_text']}${symbol['font_class']}" />`
      },
      generateIconfontIconClass(symbol) {
        console.log('---6666666-------', `icon ${iconfonts['font_family']} ${iconfonts['css_prefix_text']}${symbol['font_class']}`)
        return `${iconfonts['font_family']} ${iconfonts['css_prefix_text']}${symbol['font_class']}`
      },
      handleClipboard(text, event) {
        clipboard(text, event)
      }
    }
  }
</script>

<style lang="scss" scoped>
  .icons-container {
    margin: 10px 20px 0;
    overflow: hidden;

    .icon-item {
      margin: 20px;
      height: 85px;
      text-align: center;
      width: 100px;
      float: left;
      font-size: 30px;
      color: #24292e;
      cursor: pointer;
    }

    span {
      display: block;
      font-size: 16px;
      margin-top: 10px;
    }

    .disabled {
      pointer-events: none;
    }
  }
</style>
