<template>
  <div>
    <el-dialog
      append-to-body
      :close-on-click-modal="false"
      :visible.sync="selectRole"
      title="选择角色"
      width="300px"
      height="200px"
    >
      <el-select v-model="currentRole" style="width: 100%;">
        <el-option v-for="item in user().roles" :value="item.id" :label="item.name" />
      </el-select>
      <div slot="footer" class="dialog-footer">
        <el-button type="text" @click="cancel">取消</el-button>
        <el-button type="primary" @click="goin">确认</el-button>
      </div>
    </el-dialog>
  </div>

</template>
<script>
  import Config from '../../public/config/settings'
  import { mapActions, mapGetters } from 'vuex'

  export default {
    name: 'SsoLogin',
    data() {
      return {
        title: Config.title,
        currentRole: '',
        selectRole: false
      }
    },
    mounted() {
      this.$store.dispatch('SsoLogin', this.$route.query['token']).then(() => {
        if (this.user().roles != null && this.user().roles.length > 0) {
          if (this.user().roles.length > 1) {
            //console.log('=======login=====>', this.user())
            this.selectRole = true
          } else if (this.user().roles.length == 1) {
            this.currentRole = this.user().roles[0].id
            this.goin()
          }
        } else {
          this.$message("当前用户没有访问该系统的权限，请联系管理员！",'error')
        }
      }).catch(() => {
      })
    },
    methods: {
      goin() {
        if (!this.currentRole) {
          this.$message('请选择角色', 'error')
          return
        }
        console.log('======goin============', this.currentRole, this.user().roles.find(e => e.id == this.currentRole))
        this.$store.dispatch('setCurrentRole', this.user().roles.find(e => e.id == this.currentRole))
        this.$router.push({ path: this.redirect || '/' })
      },
      cancel() {
        this.selectRole = false
        this.loading = false
      },
      ...mapGetters(['getSmartbi', 'user']),
    ...mapActions(['setSmartbi'])
    }
  }
</script>

