import request from '@eladmin/utils/request'

export function getDepts(params) {
  return request({
    url: 'api/dept',
    method: 'get',
    params
  })
}

export function add(data) {
  return request({
    url: 'api/dept',
    method: 'post',
    data
  })
}

export function del(ids) {
  return request({
    url: 'api/dept',
    method: 'delete',
    data: ids
  })
}

export function edit(data) {
  return request({
    url: 'api/dept',
    method: 'put',
    data
  })
}
export function configDbp(data) {
  return request({
    url: 'api/dept/configDbp',
    method: 'put',
    data
  })
}

export function getDeptUser(data) {
  return request({
    url: 'system/dept/getDeptUser?' + data,
    method: 'get',
    data
  })
}

export default { add, edit, del, getDepts,configDbp, getDeptUser }
