export default function loadView(view) {
  switch (view.substring(0, view.indexOf('/'))) {
    case '@eladmin':
      console.log('--loadViewloadView------',view.substring(view.indexOf('/') + 1))
      return (resolve) => require([`@eladmin/views/${view.substring(view.indexOf('/') + 1)}`], resolve)
    case '@system':
      return (resolve) => require([`@/views/system/${view.substring(view.indexOf('/') + 1)}`], resolve)
    default:
      return (resolve) => require([`@/views/${view}`], resolve)
  }
}
