<script>
import crudDept from '../../../api/system/dept'
import qs from 'qs'
import Treeselect from '@riophae/vue-treeselect'
import '@riophae/vue-treeselect/dist/vue-treeselect.css'
import CRUD, {presenter, header, form, crud} from '@crud/crud'
import rrOperation from '@crud/RR.operation'
import crudOperation from '@crud/CRUD.operation'
import udOperation from '@crud/UD.operation'

// crud交由presenter持有
const defaultCrud = CRUD({title: '机构', url: 'system/dept/queryDepts', crudMethod: {...crudDept}})
const defaultForm = {id: null, name: null, pid: 1, enabled: 'true', code: null, bussCode: null, bussName: null}
export default {
  name: 'Dept',
  components: {Treeselect, crudOperation, rrOperation, udOperation},
  mixins: [presenter(defaultCrud), header(), form(defaultForm), crud()],
  // 设置数据字典
  dicts: ['dept_status', 'DEPT_CATEGORY'],
  data() {
    return {
      expands: ['1'],
      depts: [],
      dbpdata: {
        id: null,
        name: null,
        thirdPath: null,
        defaultApproveUser: null
      },
      dbpconfigTitle: '机构配置',
      rules: {
        name: [
          {required: true, message: '请输入名称', trigger: 'blur'}
        ],
        id: [
          {required: true, message: '请输入编号', trigger: 'blur'}
        ]
      },
      permission: {
        add: ['admin', 'dept:add'],
        edit: ['admin', 'dept:edit'],
        del: ['admin', 'dept:del']
      },
      enabledTypeOptions: [
        {key: '1', display_name: '启用'},
        {key: '0', display_name: '停用'}
      ],
      dbpconfigStatus: false,
      submitConfigStatus: 1,

      deptUsers: []
    }
  },
  created() {
    this.resetQuery()
  },
  methods: {
    resetQuery() {
      this.crud.resetQuery(false);
      this.crud.refresh()
    },
    getRowKeys: function (row) {
      return row.id
    },
    // 新增与编辑前做的操作
    [CRUD.HOOK.afterToCU](crud, form) {
      form.enabled = `${form.enabled}`
      // 获取所有部门
      crudDept.getDepts({enabled: true}).then(res => {
        this.depts = res.content
      })
    },
    // 查询前做的操作
    [CRUD.HOOK.beforeToDetail](crud, form) {
      form.enabled = `${form.enabled}`
      // 获取所有部门
      /*crudDept.getDepts({enabled: true}).then(res => {
        this.depts = res.content
      })*/
    },
    // 提交前的验证
    [CRUD.HOOK.afterValidateCU]() {
      if (!this.form.pid && this.form.id !== 1) {
        this.$message({
          message: '上级部门不能为空',
          type: 'warning'
        })
        return false
      }
      return true
    },
    // 改变状态
    changeEnabled(data, val) {
      this.$confirm('此操作将 "' + this.dict.label.dept_status[val] + '" ' + data.name + '部门, 是否继续？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        crudDept.edit(data).then(res => {
          this.crud.notify(this.dict.label.dept_status[val] + '成功', CRUD.NOTIFICATION_TYPE.SUCCESS)
        }).catch(err => {
          data.enabled = !data.enabled
          console.log(err.response.data.message)
        })
      }).catch(() => {
        data.enabled = !data.enabled
      })
    },
    checkboxT(row, rowIndex) {
      return row.id !== 1
    },
    configDbpPath(row) {
      //this.getDeptUser(row.id);
      defaultCrud.crudMethod.getDeptUser(qs.stringify({deptId: row.id})).then(res => {
        this.deptUsers = res.data
        console.log("configDbpPath   " + row.name)
        this.dbpdata.id = row.id
        this.dbpdata.name = row.name
        this.dbpdata.thirdPath = row.dbpPath
        this.dbpdata.defaultApproveUser = row.defaultApproveUserList
        this.dbpconfigStatus = true
      })
    },
    submitConfig(dbpdata) {
      this.submitConfigStatus = 2
      console.log("submitConfig  id:  " + dbpdata.id + ",thirdPath: " + dbpdata.thirdPath)
      var formData = {};
      formData.deptId = dbpdata.id
      formData.deptName = dbpdata.name
      /*if(!dbpdata.thirdPath){
        this.$message({
          message: '请输入DBP路径',
          type: 'warning'
        })
        this.submitConfigStatus = 1
        return false;
      }*/
      formData.thirdPath = dbpdata.thirdPath
      formData.defaultApproveUser = dbpdata.defaultApproveUser != null ? dbpdata.defaultApproveUser.toString() : null
      defaultCrud.crudMethod.configDbp(formData).then(res => {
        console.log("submitConfig res : " + res)
        defaultCrud.notify(res, CRUD.NOTIFICATION_TYPE.SUCCESS)
        this.dbpconfigStatus = false
        this.submitConfigStatus = 1
        defaultCrud.toQuery();
      })
    },
    cancelConfig() {
      this.dbpconfigStatus = false
      this.submitConfigStatus = 1
    }
    /*getDeptUser(deptId){
      defaultCrud.crudMethod.getDeptUser(qs.stringify({deptId:deptId})).then(res => {
        debugger;
        console.log("submitConfig res : " + res)
        this.deptUsers = res.data
      })
    }*/
  }
}
</script>

