            </el-row>
            <el-row  type="flex"  justify="center">
              <el-col :span="10">
                <el-form-item label="身份证号" prop="idcardNo"><el-input v-model="form.idcardNo" :disabled="crud.status.flag == 2"/></el-form-item>
