              </el-col>
              <el-col :span="10">
                <el-form-item label="角色" prop="roles">
                  <el-select v-model="form.roles"  multiple placeholder="请选择" @remove-tag="deleteTag" @change="changeRole" value-key="roles" :disabled="crud.status.flag == 2">
                    <el-option v-for="item in roles" :key="item.name" :disabled="!level || item.level > form.dept.deptLevel" :label="item.name" :value="item.id"/>
                  </el-select>
                </el-form-item>
