            </el-row>
            <el-row  type="flex"  justify="center">
              <el-col :span="10">
                <el-form-item label="岗位" prop="position"><el-input v-model="form.position" :disabled="crud.status.flag == 2"/></el-form-item>
                <!--   <el-form-item label="邮箱" prop="email">
                   <el-input v-model="form.email"/>
                 </el-form-item>

                <el-form-item label="岗位" prop="job.id">
                   <el-select v-model="form.job.id" style="width: 178px" placeholder="请先选择部门">
                     <el-option
                       v-for="(item, index) in jobs"
