                  <el-input v-model="form.datumPost" :disabled="crud.status.flag == 2"/>
                </el-form-item>
              </el-col>
              <el-col :span="10">
                <el-form-item label="岗位层级" prop="hirPostLevel">
                  <el-input v-model="form.hirPostLevel" :disabled="crud.status.flag == 2"/>
                </el-form-item>
              </el-col>
            </el-row>
            <el-row  type="flex"  justify="center">
              <el-col :span="10">
                <el-form-item label="职级ID" prop="grade">
                  <el-input v-model="form.grade" :disabled="crud.status.flag == 2"/>
                </el-form-item>
              </el-col>
              <el-col :span="10">
                <el-form-item label="条线" prop="lines">
                  <el-input v-model="form.lines" :disabled="crud.status.flag == 2"/>
                </el-form-item>
              </el-col>
            </el-row>
            <el-row  type="flex"  justify="center">
              <el-col :span="10">
                <el-form-item label="类型" prop="userType">
                  <el-select v-model="form.userType"  filterable placeholder="请选择" default-first-option value-key="userType"
                             :disabled="crud.status.flag == 2">
                    <el-option v-for="item in dict.USER_TYPE" :key="item.id" :label="item.label" :value="item.value"/>
                  </el-select>
                </el-form-item>
              </el-col>
              <el-col :span="10">
                <el-form-item label="手机号" prop="phone">
                  <el-input v-model.number="form.phone" :disabled="crud.status.flag == 2"/>
                </el-form-item>
              </el-col>
            </el-row>
            <el-row  type="flex"  justify="center">
              <el-col :span="10">
                <el-form-item label="短ID" prop="stId"><el-input v-model="form.stId" :disabled="crud.status.flag == 2"/></el-form-item>
              </el-col>
              <el-col :span="10">
                <el-form-item label="电话" prop="tel"><el-input v-model="form.tel" :disabled="crud.status.flag == 2"/></el-form-item>
              </el-col>
            </el-row>
            <el-row  type="flex"  justify="center">
              <el-col :span="10">
                <el-form-item label="柜员编号" prop="tellerId">
                  <el-input v-model="form.tellerId" :disabled="crud.status.flag == 2"/>
                </el-form-item>
              </el-col>
              <el-col :span="10">
                <el-form-item label="工号级别" prop="haoLevel"><el-input v-model="form.haoLevel" :disabled="crud.status.flag == 2"/></el-form-item>
              </el-col>
            </el-row>
            <el-row  type="flex"  justify="center">
              <el-col :span="10">
                <el-form-item label="支撑系统" prop="serveSystem"><el-input v-model="form.serveSystem" :disabled="crud.status.flag == 2"/></el-form-item>
              </el-col>
              <el-col :span="10">
                <el-form-item label="支撑对象" prop="serveObject"><el-input v-model="form.serveObject" :disabled="crud.status.flag == 2"/></el-form-item>
              </el-col>
            </el-row>
            <el-row  type="flex"  justify="center">
              <el-col :span="10">
                <el-form-item label="状态">
                  <el-radio-group v-model="form.enabled" :disabled="form.id === user.id || crud.status.flag == 2">
                    <el-radio v-for="item in dict.user_status" :key="item.id" :label="item.value">{{ item.label }}</el-radio>
                  </el-radio-group>
                </el-form-item>
              </el-col>
              <el-col :span="10">

              </el-col>
            </el-row>
          </el-form>
          <div slot="footer" class="dialog-footer">
            <el-button v-if="crud.status.flag == 0 || crud.status.flag == 1" type="success" plain round @click="crud.cancelCU">取消</el-button>
            <el-button v-if="crud.status.flag == 0 || crud.status.flag == 1" :loading="crud.cu === 2" type="success" round @click="crud.submitCU">确认</el-button>
            <el-button v-if="crud.status.flag == 2 " type="success" plain round @click="crud.cancelCU">关闭</el-button>
          </div>
        </el-dialog>
        <!--表格渲染-->
        <el-table ref="table" v-loading="crud.loading" :data="crud.data" style="width: 100%;" @selection-change="crud.selectionChangeHandler">
          <el-table-column :selectable="checkboxT" type="selection" width="55"/>
          <el-table-column v-if="columns.visible('username')" :show-overflow-tooltip="true" prop="username" label="用户名"/>
          <el-table-column v-if="columns.visible('userNo')" :show-overflow-tooltip="true" prop="userNo" label="用户编号"/>
          <el-table-column v-if="columns.visible('nickName')" :show-overflow-tooltip="true" prop="nickName" label="姓名"/>
          <el-table-column v-if="columns.visible('sex')" prop="sex" label="性别"/>
          <el-table-column v-if="columns.visible('phone')" :show-overflow-tooltip="true" prop="phone" width="100" label="电话"/>
          <el-table-column v-if="columns.visible('email')" :show-overflow-tooltip="true" width="125" prop="email" label="邮箱"/>
          <el-table-column v-if="columns.visible('dept')" :show-overflow-tooltip="true" width="110" prop="dept" label="机构">
            <template slot-scope="scope"><div>{{ scope.row.dept.name }}</div></template>
          </el-table-column>
          <el-table-column v-if="columns.visible('roles')" :show-overflow-tooltip="true" width="110" prop="dept" label="角色">
            <template slot-scope="scope"><div>{{ scope.row.roles.map(e => e.name).join(',') }}</div></template>
          </el-table-column>
          <el-table-column v-if="columns.visible('userType')" prop="userType" label="用户类型">
            <template slot-scope="scope">{{ dict.label.USER_TYPE[scope.row.userType] }}</template>
          </el-table-column>
          <el-table-column v-if="columns.visible('tellerId')" :show-overflow-tooltip="true" width="125" prop="tellerId" label="柜员编号"/>
          <el-table-column v-if="columns.visible('enabled')" label="状态" align="center" prop="enabled">
            <template slot-scope="scope">
              <el-tooltip effect="dark" :content="dict.label['user_status'][scope.row.enabled]" placement="top-start">
                <el-switch v-model="scope.row.enabled" :disabled="user.id === scope.row.id" @change="changeEnabled(scope.row, scope.row.enabled)"/>
              </el-tooltip>
            </template>
          </el-table-column>
          <el-table-column v-if="columns.visible('createTime')" :show-overflow-tooltip="true" prop="createTime" width="140" label="创建日期">
            <template slot-scope="scope"><span>{{ parseTime(scope.row.createTime) }}</span></template>
          </el-table-column>
          <el-table-column v-permission="['admin','user:edit','user:del']" label="操作" width="125" align="center" fixed="right">
            <!--            <template slot-scope="scope">
                          <udOperation :data="scope.row" :permission="permission" :disabled-dle="scope.row.id === user.id"/>
                        </template>-->
            <template slot-scope="scope">
              <el-button class="filter-item" type="success"    @click="crud.toDetail(scope.row)">详情</el-button>
            </template>
          </el-table-column>
        </el-table>
        <!--分页组件-->
        <pagination/>
      </el-col>
    </el-row>
  </div>
</template>

<script>
import crudUser from '../../../api/system/user'
import { isvalidPhone } from '@eladmin/utils/validate'
import { getDepts } from '../../../api/system/dept'
import { getAll, getLevel } from '../../../api/system/role'
import { getAllJob } from '../../../api/system/job'
import CRUD, { presenter, header, form, crud } from '@crud/crud'
import rrOperation from '@crud/RR.operation'
import crudOperation from '@crud/CRUD.operation'
import udOperation from '@crud/UD.operation'
import pagination from '@crud/Pagination'
import Treeselect from '@riophae/vue-treeselect'
import { mapGetters } from 'vuex'
import '@riophae/vue-treeselect/dist/vue-treeselect.css'
import request from '@eladmin/utils/request'
import qs from 'qs'

let userRoles = []
// crud交由presenter持有
const defaultCrud = CRUD({ title: '用户', url: 'api/users', crudMethod: { ...crudUser } })
const defaultForm = {
  id: null,
  userType: '0',
  username: null,
  nickName: null,
  sex: '男',
  email: null,
  enabled: 'false',
  roles: [],
  job: { id: null },
  dept: { id: null },
  phone: null
}
export default {
  name: 'User',
  components: { Treeselect, crudOperation, rrOperation, udOperation, pagination },
  mixins: [presenter(defaultCrud), header(), form(defaultForm), crud()],
  // 数据字典
  dicts: ['user_status', 'USER_TYPE'],
  data() {
    // 自定义验证
    const validPhone = (rule, value, callback) => {
      if (!value) {
        callback(new Error('请输入电话号码'))
      } else if (!isvalidPhone(value)) {
        callback(new Error('请输入正确的11位手机号码'))
      } else {
        callback()
      }
    }
    return {
      height: document.documentElement.clientHeight - 180 + 'px;',
      deptName: '', depts: [], deptDatas: [], jobs: [], level: 3, roles: [],
      defaultProps: { children: 'children', label: 'name' },
      permission: {
        add: ['admin', 'user:add'],
        edit: ['admin', 'user:edit'],
        del: ['admin', 'user:del'],
        resetpwd: ['admin', 'user:resetpwd']
      },
      enabledTypeOptions: [
        { key: 'true', display_name: '启用' },
        { key: 'false', display_name: '停用' }
      ],
      userTypeOptions: [
        { key: '0', user_type_select: '临时' },
        { key: '1', user_type_select: '正式' }
      ],
      rules: {
        username: [
          { required: true, message: '请输入用户名', trigger: 'blur' },
          { min: 2, max: 20, message: '长度在 2 到 20 个字符', trigger: 'blur' }
        ],
        userType: [
          { required: true, message: '请选择用户类型', trigger: 'blur' }
        ],
        nickName: [
          { required: true, message: '请输入用户昵称', trigger: 'blur' },
          { min: 2, max: 20, message: '长度在 2 到 20 个字符', trigger: 'blur' }
        ],
        dept: {
          id: [
            { required: true, message: '机构不能为空', trigger: 'change' }
          ]
        }
      },
      resetLoading: false
    }
  },
  computed: {
    ...mapGetters([
      'user'
    ])
  },
  created() {
    // console.log("初始化查询表单传输数据！" + this.form.enabled)
    this.query.enabled = 'true'
    this.crud.refresh()
    this.$nextTick(() => {
      this.getDeptDatas()
      this.crud.toQuery()
      this.crud.msg.add = '新增成功，默认密码：123456'
    })
  },
  mounted: function() {
    const that = this
    window.onresize = function temp() {
      that.height = document.documentElement.clientHeight - 180 + 'px;'
    }
  },
  methods: {
    changeRole(value) {
      userRoles = []
      value.forEach(function(data, index) {
        const role = { id: data }
        userRoles.push(role)
      })
    },
    [CRUD.HOOK.afterAddError](crud) {
      this.afterErrorMethod(crud)
    },
    [CRUD.HOOK.afterEditError](crud) {
      this.afterErrorMethod(crud)
    },
    afterErrorMethod(crud) {
      // 恢复select
      const initRoles = []
      userRoles.forEach(function(role, index) {
        initRoles.push(role.id)
      })
      crud.form.roles = initRoles
    },
    deleteTag(value) {
      userRoles.forEach(function(data, index) {
        if (data.id === value) {
          userRoles.splice(index, value)
        }
      })
    },
    // 新增与编辑前做的操作
    [CRUD.HOOK.afterToCU](crud, form) {
      this.getDepts()
      this.getRoles()
      this.getRoleLevel()
      form.enabled = form.enabled.toString()
      if (!form.dept.id && this.query.deptId) {
        form.dept.id = this.query.deptId
      }
    },
    // 打开编辑弹窗前做的操作
    [CRUD.HOOK.beforeToEdit](crud, form) {
      // this.getJobs(this.form.dept.id)
      userRoles = []
      const roles = []
      form.roles.forEach(function(role, index) {
        roles.push(role.id)
        // 初始化编辑时候的角色
        const rol = { id: role.id }
        userRoles.push(rol)
      })
      form.roles = roles
    },
    // 提交前做的操作
    [CRUD.HOOK.afterValidateCU](crud) {
      console.log('用户类型：' + crud.form.userType)
      console.log('用户名：' + crud.form.username)
      if (crud.form.userType === '0') {
        const nameResult = crud.form.username.substring(0, 3)
        if (nameResult != 'DAP') {
          crud.form.username = 'DAP' + crud.form.username
        }
      }
      if (!crud.form.dept.id) {
        this.$message({
          message: '部门不能为空',
          type: 'warning'
        })
        return false
      }
      crud.form.roles = userRoles
      return true
    },
    // 获取左侧部门数据
    getDeptDatas() {
      const sort = 'id,desc'
      const params = { sort: sort }
      if (this.deptName) {
        params['name'] = this.deptName
      }
      getDepts(params).then(res => {
        this.deptDatas = res.content
      })
    },
    // 获取弹窗内部门数据
    getDepts() {
      getDepts({ enabled: true }).then(res => {
        this.depts = res.content
      })
    },
    // 切换部门
    handleNodeClick(data) {
      if (data.pid === 0) {
        this.query.deptId = null
      } else {
        this.query.deptId = data.id
      }
      this.crud.toQuery()
    },
    // 改变状态
    changeEnabled(data, val) {
      this.$confirm('此操作将 "' + this.dict.label.user_status[val] + '" ' + data.username + ', 是否继续？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        crudUser.edit(data).then(res => {
          this.crud.notify(this.dict.label.user_status[val] + '成功', CRUD.NOTIFICATION_TYPE.SUCCESS)
        }).catch(() => {
          data.enabled = !data.enabled
        })
      }).catch(() => {
        data.enabled = !data.enabled
      })
    },
    // 获取弹窗内角色数据
    getRoles() {
      getAll().then(res => {
        this.roles = res
      }).catch(() => {
      })
    },
    // 获取弹窗内岗位数据
    getJobs(id) {
      getAllJob(id).then(res => {
        this.jobs = res.content
      }).catch(() => {
      })
    },
    // 点击部门搜索对应的岗位
    selectFun(node, instanceId) {
      // console.log('=======selectFun========', node, instanceId)
      // this.level = node.deptLevel
      // this.getJobs(node.id)
      // this.form.job.id = null
    },
    // 获取权限级别
    getRoleLevel() {
      getLevel().then(res => {
        this.level = res.level
      }).catch(() => {
      })
    },
    checkboxT(row, rowIndex) {
      return row.id !== this.user.id
    },

    doResetPwd(datas) {
      this.$confirm(`确认重置选中的${datas.length}个用户?`, '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        this.resetPwd(datas)
      }).catch(() => {
      })
    },
    // 重置密码
    resetPwd(key, index) {
      const ids = []
      if (key instanceof Array) {
        key.forEach(val => {
          ids.push(val.id)
        })
      } else {
        ids.push(key.id)
      }
      this.resetLoading = true
      request({
        url: '/api/users/resetPwd',
        method: 'post',
        data: ids
      }).then(res => {
        this.resetLoading = false
        this.$notify({
          title: '重置成功',
          type: 'success',
          duration: 2500
        })
      })
    }
  }
}
</script>

