import request from '@/utils/request'

const url = 'v1/datasource/indDataColumns'

export function add(data) {
  return request({
    url: url,
    method: 'post',
    data
  })
}

export function del(ids) {
  return request({
    url: url,
    method: 'delete',
    data: ids
  })
}

export function edit(data) {
  return request({
    url: url,
    method: 'put',
    data
  })
}

export function getColumnListByDataSourceIdAndTableName(dataSourceId, tableName) {
  return request({
    url: url + '/' + dataSourceId + '/' + tableName,
    method: 'get'
  })
}

export function getColumnListByDatabase(dataSourceId, tableName) {
  return request({
    url: url + '/database/' + dataSourceId + '/' + tableName,
    method: 'get'
  })
}

export function saveColumnList(data) {
  return request({
    url: url + '/saveColumnList',
    method: 'post',
    data
  })
}

export default { url, method: { add, edit, del }}
