import request from '@/utils/request'

const url = 'v1/datasource/indDataTables'

export function add(data) {
  return request({
    url: url,
    method: 'post',
    data
  })
}

export function del(ids) {
  return request({
    url: url,
    method: 'delete',
    data: ids
  })
}

export function edit(data) {
  return request({
    url: url,
    method: 'put',
    data
  })
}

export function getTableList(dataSourceId) {
  return request({
    url: url + '/database/' + dataSourceId,
    method: 'get'
  })
}

export function getTableListById(dataSourceId) {
  return request({
    url: url + '/datasource/' + dataSourceId,
    method: 'get'
  })
}

export default { url, method: { add, edit, del }}
