import request from '@/utils/request'

const url = 'v1/datasource/indDatasources'

export function add(data) {
  return request({
    url: url,
    method: 'post',
    data
  })
}

export function test(data) {
  return request({
    url: url + '/test',
    method: 'post',
    data
  })
}

export function saveTableAndColumnForList(data, dataSourceId) {
  return request({
    url: url + '/saveTableAndColumnForList/' + dataSourceId,
    method: 'post',
    data
  })
}

export function sqlCheck(data) {
  return request({
    url: 'v1/database/sqlCheck',
    method: 'post',
    data
  })
}

export function del(ids) {
  return request({
    url: url,
    method: 'delete',
    data: ids
  })
}

export function edit(data) {
  return request({
    url: url,
    method: 'put',
    data
  })
}

export function getAll(data) {
  return request({
    url: url + '/source/all',
    method: 'get',
  })
}

export default { url, method: { add, edit, del, getAll}}
