<template>
  <div style="padding: 0 15px;" @click="toggleClick">
    <div class="hamburger"><i :class="isActive?'el-icon-d-arrow-left':'el-icon-d-arrow-right'"/></div>
  </div>
</template>

<script>
  export default {
    name: 'Hamburger',
    props: {
      isActive: {
        type: Boolean,
        default: false
      }
    },
    methods: {
      toggleClick() {
        this.$emit('toggleClick')
      }
    }
  }
</script>

