<template>
  <section class="app-main">
    <transition name="fade-transform" mode="out-in">
      <div v-if="keepAlive">
        <keep-alive>
          <router-view :key="key"></router-view>
        </keep-alive>
      </div>
      <div v-if="!keepAlive">
        <router-view :key="key"></router-view>
      </div>
    </transition>
    <div v-if="$store.state.settings.showFooter" id="el-main-footer">
      <span v-html="$store.state.settings.footerTxt" />
      <span> â‹… </span>
      <a href="http://www.pactera.com" target="_blank">{{
        $store.state.settings.caseNumber
      }}</a>
    </div>
  </section>
</template>

<script>
export default {
  name: "AppMain",
  computed: {
    keepAlive() {
      return this.$store.getters.keepAlive;
    },
    cachedViews() {
      // console.log('-------cachedViews:', this.$store.state.tagsView.cachedViews)
      return this.$store.state.tagsView.cachedViews.filter(
        (e) => e.path == this.$route.path
      );
    },
    key() {
      return this.$route.path;
    },
  },
};
</script>
