<template>
  <div v-if="!item.hidden" class="menu-wrapper">
    <template
      v-if="hasOneShowingChild(item.children,item) && (!onlyOneChild.children||onlyOneChild.noShowingChildren)&&!item.alwaysShow"
    >
      <app-link v-if="onlyOneChild.meta" :to="resolvePath(onlyOneChild.path,onlyOneChild)">
        <el-menu-item
          :index="resolvePath(onlyOneChild.path,onlyOneChild)"
          :class="{'submenu-title-noDropdown':!isNest}"
        >
          <item :icon="onlyOneChild.meta.icon||(item.meta&&item.meta.icon)" :title="onlyOneChild.meta.title" />
        </el-menu-item>
      </app-link>
    </template>

    <el-submenu v-else ref="subMenu" :index="resolvePath(item.path,item)" popper-append-to-body style="padding-bottom: 10px">
      <template slot="title">
        <item v-if="item.meta" :icon="item.meta && item.meta.icon" :title="item.meta.title" :isCollapse="isCollapse" />
      </template>
      <sidebar-item
       :isCollapse="isCollapse"
        v-for="child in item.children"
        :key="child.meta.id||child.path"
        :is-nest="true"
        :item="child"
        :base-path="resolvePath(child.path,child)"
        class="nest-menu"
      />
    </el-submenu>
  </div>
</template>

<script>
  import path from 'path'
  import { isExternal } from '@eladmin/utils/validate'
  import Item from './Item'
  import AppLink from './Link'
  import FixiOSBug from './FixiOSBug'

  export default {
    name: 'SidebarItem',
    components: { Item, AppLink },
    mixins: [FixiOSBug],
    props: {
      // route object
      item: {
        type: Object,
        required: true
      },
      isNest: {
        type: Boolean,
        default: false
      },
      basePath: {
        type: String,
        default: ''
      },
      isCollapse: {
        type: Boolean,
        // default: null
      }
    },
    data() {
      // To fix https://github.com/PanJiaChen/vue-admin-template/issues/237
      // TODO: refactor with render function
      this.onlyOneChild = null
      return {}
    },
    mounted() {
      // console.log('========1111====>>>>', this.item.component && this.item.component.name != 'Layout' && this.item.component.name != 'Submenu')
      // if (this.item.meta) {
      // console.log('========2222====>>>>', this.item.meta.title)
      // }
    },
    methods: {
      hasOneShowingChild(children = [], parent) {
        /* console.log('----------->>>>', parent.meta.title, parent, parent.component && parent.component.name != 'Layout' && parent.component.name != 'Submenu')
         if (parent.component && parent.component.name != 'Layout' && parent.component.name != 'Submenu') {
           this.onlyOneChild = { ...parent, path: '', noShowingChildren: true }
           parent.alwaysShow = false
           return true
         }*/
        const showingChildren = children.filter(item => {
          if (item.hidden) {
            return false
          } else {
            // Temp set(will be used if only has one showing child)
            this.onlyOneChild = item
            return true
          }
        })
        // When there is only one child router, the child router is displayed by default
        if (showingChildren.length === 1) {
          // console.log('=======<<<>>', showingChildren[0].meta.title, showingChildren, this.onlyOneChild)
          if (this.onlyOneChild instanceof Array) {
            this.onlyOneChild = this.onlyOneChild[0]
          }
          if (this.onlyOneChild.component && this.onlyOneChild.component.name != 'Layout' && this.onlyOneChild.component.name != 'Submenu') {
            this.onlyOneChild.noShowingChildren = true
          }
          return true
        }

        // Show parent if there are no child router to display
        if (showingChildren.length === 0) {
          // console.log('-------<<<>>', parent.meta.title, parent)
          this.onlyOneChild = { ...parent, path: '', noShowingChildren: true }
          return true
        }

        return false
      },
      resolvePath(routePath, item) {
        // console.log('--------sidebaritem------->>', routePath, item)
        if (isExternal(routePath)) {
          return routePath
        }
        if (isExternal(this.basePath)) {
          return this.basePath
        }
        if (item.meta && item.meta.cparam && routePath && routePath.indexOf(item.meta.cparam) < 0) {
          routePath = routePath + item.meta.cparam
        }
        // console.log(' hhhhhhhhhhhhhhhhhhhhh>>>>', this.basePath, '   routePath>', routePath)
        return path.resolve(this.basePath, routePath)
      }
    }
  }
</script>
