<template>
  <div
    :class="{ 'has-logo': showLogo }"
    :style="
      isCollapse
        ? 'width:48px!important;position:relative'
        : 'width:220px!important;position:relative'
    "
  >
    <!-- <logo v-if="showLogo" :collapse="true" /> -->
    <div
      style="
        height: 56px;
        display: flex;
        align-items: center;
        padding: 0 20px;
        cursor: pointer;
      "
      :class="{ changeCollapse: isCollapse }"
      @click="changeCollapse"
    >
      <i
        v-if="!isCollapse"
        class="el-icon-s-unfold"
        style="font-size: 16px; color: #000"
      ></i>
      <i
        v-if="isCollapse"
        class="el-icon-s-fold"
        style="font-size: 16px; color: #000"
      ></i>
    </div>
    <el-scrollbar
      class="sidebar-scrollbar"
      :class="{ isCollapse: isCollapse }"
      wrap-class="scrollbar-wrapper"
    >
      <el-menu
        :default-active="activeMenu"
        :collapse="isCollapse"
        :background-color="variables.menuBg"
        :text-color="variables.menuText"
        :unique-opened="$store.state.settings.uniqueOpened"
        :active-text-color="variables.menuActiveText"
        :collapse-transition="false"
        mode="vertical"
      >
        <sidebar-item
          v-for="route in permission_routers"
          :key="route.path"
          :item="route"
          :base-path="route.path"
        />
      </el-menu>
    </el-scrollbar>
    <div class="collapseIcon" @click="changeCollapse">
      <i class="el-icon-arrow-right" style="font-size: 16px; color: #fff" v-if="isCollapse"></i>
      <i class="el-icon-arrow-left" style="font-size: 16px; color: #fff" v-else></i>
    </div>
  </div>
</template>

<script>
import { mapGetters } from "vuex";
import Logo from "./Logo";
import SidebarItem from "./SidebarItem";

export default {
  components: { SidebarItem, Logo },
  data() {
    return {
      isCollapse: false,
    };
  },
  computed: {
    ...mapGetters(["permission_routers", "sidebar", "getTheme"]),
    activeMenu() {
      const route = this.$route;
      const { meta, path } = route;
      // if set path, the sidebar will highlight the path you set
      if (meta.activeMenu) {
        return meta.activeMenu;
      }
      return path;
    },
    showLogo() {
      return this.$store.state.settings.sidebarLogo;
    },
    variables() {
      return () =>
        require(`../../../../public/themes/${this.getStyle.style}/styles/variables.scss`);
    },
    // isCollapse() {
    //   return false;
    // }
  },
  methods: {
    changeCollapse() {
      this.isCollapse = !this.isCollapse;
      this.$emit("getIsCollapse", this.isCollapse);
    },
  },
};
</script>

<style scoped lang="scss">
// 菜单样式调整
$common-green-normal: rgba(97, 189, 83, 1);
$common-green-deep: rgba(69, 161, 55, 1);
$common-green-light: rgba(137, 209, 125, 1);
.collapseIcon {
  position: absolute;
  width: 16px;
  height: 60px;
  top: 50%;
  right: -15px;
  background: $common-green-light;
  border-radius: 5px;
  z-index: 9;
  line-height: 70px;
  text-align: center;
  cursor: pointer;
}
.changeCollapse {
  background: $common-green-deep;
  i {
    color: #fff !important;
  }
}
.isCollapse {
  z-index: 100;
  /deep/ .scrollbar-wrapper {
    background: $common-green-normal;
  }
  /deep/ .el-menu--collapse {
    background: $common-green-normal;
    .is-active {
      background: $common-green-deep;
    }
  }
  /deep/ .el-submenu__title {
    color: #fff !important;
  }
  /deep/ .el-submenu__title:hover {
    color: $common-green-normal !important;
    background: $common-green-deep !important;
  }
}

.el-menu-vertical-demo:not(.el-menu--collapse) {
  width: 200px;
  min-height: 400px;
}
</style>
<style>
.el-submenu__icon-arrow.el-icon-arrow-right {
  display: none !important;
}
</style>