<template>
  <div :class="classObj" class="app-wrapper">
    <div
      v-if="device === 'mobile' && sidebar.opened"
      class="drawer-bg"
      @click="handleClickOutside"
    />
    <navbar />
    <div :class="{ hasTagsView: needTagsView }" class="container">
      <sidebar class="sidebar-container" @getIsCollapse="getIsCollapse" />
      <div
        class="main-container"
        :class="
          isCollapse ? 'main-container-isCollapse' : 'main-container-collapse'
        "
      >
        <!-- <div :class="{'fixed-header':fixedHeader}"> -->
        <div
          class="fixed-header"
          :class="
            isCollapse ? 'fixed-header-isCollapse' : 'fixed-header-collapse'
          "
        >
          <!-- <navbar /> -->
          <tags-view v-if="needTagsView" />
        </div>
        <app-main />
      </div>
      <right-panel v-if="showSettings">
        <settings />
      </right-panel>
    </div>
  </div>
</template>

<script>
import RightPanel from "@eladmin/components/RightPanel";
import { AppMain, Navbar, Settings, Sidebar, TagsView } from "./components";
import ResizeMixin from "./mixin/ResizeHandler";
import { mapState, mapGetters } from "vuex";

export default {
  name: "Layout",
  components: {
    AppMain,
    Navbar,
    RightPanel,
    Settings,
    Sidebar,
    TagsView,
  },
  mixins: [ResizeMixin],
  data() {
    return {
      isCollapse: null,
    };
  },
  computed: {
    ...mapState({
      sidebar: (state) => state.app.sidebar,
      device: (state) => state.app.device,
      showSettings: (state) => state.settings.showSettings,
      needTagsView: (state) => state.settings.tagsView,
      fixedHeader: (state) => state.settings.fixedHeader,
    }),
    classObj() {
      return {
        hideSidebar: false,
        openSidebar: true,
        withoutAnimation: this.sidebar.withoutAnimation,
        mobile: this.device === "mobile",
      };
    },
  },
  methods: {
    handleClickOutside() {
      this.$store.dispatch("app/closeSideBar", { withoutAnimation: false });
    },
    ...mapGetters(["user"]),
    getIsCollapse(val) {
      this.isCollapse = val;
    },
  },
};
</script>

<style lang="scss" scoped>
.sidebar-container {
  overflow: inherit !important;
}
</style>
