<template>
  <el-card :class="className" :style="{height:height,width:width}">
    <div slot="header" class="clearfix">
      <span class="card-title-text">产品余额</span>
      <span class="card-title-unit">(万元)</span>
    </div>
    <el-table
      :data="cardData"
      border
      class="card-main card-main-table card-product-bal"
      header-row-class-name="card-main-table-header"
      row-class-name="card-main-table-row">
      <el-table-column
        prop="productType"
        header-align="center"
        align="left"
        label="产品类型" width="95px" :resizable="false">
      </el-table-column>
      <el-table-column
        prop="personalBal"
        header-align="center"
        align="right"
        show-overflow-tooltip
        label="个人余额"
        :resizable="false"
        :formatter="numberColumnFormat">
      </el-table-column>
      <el-table-column
        prop="enterpriseBal"
        header-align="center"
        align="right"
        show-overflow-tooltip
        label="对公余额"
        :resizable="false"
        :formatter="numberColumnFormat">
      </el-table-column>
      <el-table-column
        prop="totalBal" show-overflow-tooltip
        header-align="center"
        align="right"
        show-overflow-tooltip
        label="总余额"
        :resizable="false"
        :formatter="numberColumnFormat">
      </el-table-column>
    </el-table>
  </el-card>
</template>

<script>
  import { productBal } from '../../../api/dashboard/dashboard'
  import { getYesterday } from '@eladmin/utils/data_format'

  export default {
    name: 'ProductBal',
    data() {
      return {
        cardData: []
      }
    },
    props: {
      className: {
        type: String,
        default: 'box-card'
      },
      width: {
        type: String,
        default: '100%'
      },
      height: {
        type: String,
        default: '100%'
      },
      autoResize: {
        type: Boolean,
        default: true
      },
      date: {
        type: String,
        default: getYesterday()
      }
    },
    mounted() {
      productBal(this.date).then(res => {
          this.cardData = res.data
        }
      )
    }
  }
</script>

