export function loadView(view) {
  switch (view.substring(0, view.indexOf('/'))) {
    case '@eladmin':
      console.log('--loadViewloadView------',view.substring(view.indexOf('/') + 1))
      return (resolve) => require([`eladmin-ui/src/views/${view.substring(view.indexOf('/') + 1)}`], resolve)
    case '@system':
      return (resolve) => require([`eladmin-system/src/views/system/${view.substring(view.indexOf('/') + 1)}`], resolve)
    case '@workflow':
      return (resolve) => require([`@/views/activiti/${view.substring(view.indexOf('/') + 1)}`], resolve)
    default:
      return (resolve) => require([`@/views/${view}`], resolve)
  }
}
